// frontend/src/components/HomePage.jsx
import React from "react";
import { Link } from "react-router-dom";
import Streamings from "./Streamings";

// Normalizamos para que siempre caigan al final sin importar mayúsculas/espacios
const SPECIAL_TAIL = new Set(["bard", "paladin", "artist", "valkyrie"]);
const isTailClass = (clsName) =>
  typeof clsName === "string" && SPECIAL_TAIL.has(clsName.trim().toLowerCase());

// Ordena: primero NO especiales, al final especiales (orden estable)
const sortTeam = (arr = []) => {
  const withIndex = arr.map((c, i) => ({ c, i }));
  withIndex.sort((a, b) => {
    const aIsTail = isTailClass(a.c?.class?.name);
    const bIsTail = isTailClass(b.c?.class?.name);
    if (aIsTail !== bIsTail) return aIsTail ? 1 : -1;
    return a.i - b.i;
  });
  return withIndex.map((x) => x.c);
};

// ---- Helpers de fecha/hora ----
const mmddRe = /^(0[1-9]|1[0-2])-(0[1-9]|[12]\d|3[01])$/;
const ymdRe  = /^(\d{4})-(0[1-9]|1[0-2])-(0[1-9]|[12]\d|3[01])$/;

// Formatea a MM-DD (solo mes y día), soporta YYYY-MM-DD y MM-DD
const formatDayMonth = (value) => {
  if (!value) return "Fecha no asignada";
  const s = String(value).trim();
  if (mmddRe.test(s)) { const [m, d] = s.split("-"); return `${m.padStart(2,"0")}-${d.padStart(2,"0")}`; }
  const ymd = s.match(ymdRe);
  if (ymd) { const [, , m, d] = ymd; return `${m}-${d}`; }
  const d = new Date(s);
  if (!isNaN(d)) { const mm = String(d.getMonth()+1).padStart(2,"0"); const dd = String(d.getDate()).padStart(2,"0"); return `${mm}-${dd}`; }
  return s;
};

// Clave de orden para la fecha (ignorando año)
const dateKeyFromDate = (dateStr) => {
  if (!dateStr) return Number.POSITIVE_INFINITY;
  const s = String(dateStr).trim();
  if (mmddRe.test(s)) { const [m,d]=s.split("-"); return parseInt(`${m}${d}`,10); }
  const ymd = s.match(ymdRe);
  if (ymd) { const [, , m, d]=ymd; return parseInt(`${m}${d}`,10); }
  const d = new Date(s);
  if (!isNaN(d)) { const mm=String(d.getMonth()+1).padStart(2,"0"); const dd=String(d.getDate()).padStart(2,"0"); return parseInt(`${mm}${dd}`,10); }
  return Number.POSITIVE_INFINITY;
};

// Minutos desde "00:00"
const minutesFromTime = (timeStr) => {
  if (!timeStr) return Number.POSITIVE_INFINITY;
  const s = String(timeStr).trim();
  let m = s.match(/^([01]?\d|2[0-3]):([0-5]\d)$/);
  if (m) { const hh=parseInt(m[1],10), mm=parseInt(m[2],10); return hh*60+mm; }
  m = s.match(/^(\d{3,4})$/);
  if (m) { const raw=m[1]; const hh=parseInt(raw.length===3?raw.slice(0,1):raw.slice(0,2),10);
           const mm=parseInt(raw.length===3?raw.slice(1):raw.slice(2),10);
           if (hh>=0&&hh<=23&&mm>=0&&mm<=59) return hh*60+mm; }
  m = s.match(/^([01]?\d|2[0-3])$/);
  if (m) { const hh=parseInt(m[1],10); return hh*60; }
  return Number.POSITIVE_INFINITY;
};

// Date desde {date,time}
const dateFromCard = (dateStr, timeStr) => {
  if (!dateStr) return null;
  const now=new Date(); let y=now.getFullYear(), m, d;
  if (mmddRe.test(dateStr)) { const [mm,dd]=dateStr.split("-").map(n=>parseInt(n,10)); m=mm; d=dd; }
  else {
    const ymd = dateStr.match(ymdRe);
    if (ymd) { y=parseInt(ymd[1],10); m=parseInt(ymd[2],10); d=parseInt(ymd[3],10); }
    else { const free=new Date(dateStr); if (isNaN(free)) return null; y=free.getFullYear(); m=free.getMonth()+1; d=free.getDate(); }
  }
  let hh=0, mm=0;
  if (typeof timeStr==="string" && timeStr.trim()) {
    const s=timeStr.trim(); let m1=s.match(/^([01]?\d|2[0-3]):([0-5]\d)$/);
    if (m1){ hh=parseInt(m1[1],10); mm=parseInt(m1[2],10); }
    else { m1=s.match(/^(\d{3,4})$/);
      if (m1){ const raw=m1[1]; hh=parseInt(raw.length===3?raw.slice(0,1):raw.slice(0,2),10);
               mm=parseInt(raw.length===3?raw.slice(1):raw.slice(2),10); }
      else { m1=s.match(/^([01]?\d|2[0-3])$/); if (m1) hh=parseInt(m1[1],10); }
    }
  }
  const dt=new Date(y,(m||1)-1,d||1,hh,mm,0,0);
  return isNaN(dt) ? null : dt;
};

// Estado de card
const getCardStatus = (card, nowTs) => {
  const start = dateFromCard(card?.date, card?.time);
  if (!start) return "upcoming";
  const startTs=start.getTime(), endTs=startTs+60*60*1000;
  if (nowTs>=endTs) return "expired";
  if (nowTs>=startTs) return "live";
  return "upcoming";
};

const CharItem = ({ char }) => {
  if (!char) return null;
  return (
    <div style={{
      padding:"10px", borderRadius:"10px", backgroundColor:"#f8fafc",
      border:"1px solid #e2e8f0", display:"flex", flexDirection:"column",
      alignItems:"center", gap:6, textAlign:"center",
    }} title={char.name}>
      {char.class?.image ? (
        <img src={char.class.image} alt={char.class?.name||"Clase"}
             style={{ width:32, height:32, borderRadius:8, objectFit:"cover" }}
             loading="lazy" decoding="async"/>
      ) : (
        <div aria-hidden style={{
          width:32, height:32, borderRadius:8, background:"#e5e7eb",
          display:"grid", placeItems:"center", fontSize:12, color:"#6b7280",
        }}>?</div>
      )}
      <div style={{
        fontWeight:700, fontSize:13, color:"#0f172a",
        whiteSpace:"nowrap", overflow:"hidden", textOverflow:"ellipsis", width:"100%",
      }}>{char.name}</div>
    </div>
  );
};

// ---- Canales Twitch
const TWITCH_LINKS = [
  "https://www.twitch.tv/trickmasteh",
  "https://www.twitch.tv/blawbberto",
  "https://www.twitch.tv/kaosniight",
  "https://www.twitch.tv/khangsoofly",
  "https://www.twitch.tv/faaaxy",
  "https://www.twitch.tv/takeshijoga",
];

export default function HomePage({ cards = [], loading, error }) {
  const [nowTs, setNowTs] = React.useState(() => Date.now());
  const [hasLive, setHasLive] = React.useState(false); // <- para ocultar/mostrar sidebar

  React.useEffect(() => {
    const id = setInterval(() => setNowTs(Date.now()), 30_000);
    return () => clearInterval(id);
  }, []);

  const sortedCards = React.useMemo(() => {
    return [...cards].sort((a, b) => {
      const da = dateKeyFromDate(a?.date);
      const db = dateKeyFromDate(b?.date);
      if (da !== db) return da - db;
      const ta = minutesFromTime(a?.time);
      const tb = minutesFromTime(b?.time);
      if (ta !== tb) return ta - tb;
      const at = (a?.title || "").toLowerCase();
      const bt = (b?.title || "").toLowerCase();
      if (at < bt) return -1; if (at > bt) return 1; return 0;
    });
  }, [cards]);

  const visibleCards = React.useMemo(
    () => sortedCards.filter((c) => getCardStatus(c, nowTs) !== "expired"),
    [sortedCards, nowTs]
  );

  return (
    <div style={{
      maxWidth: "1360px",  // <- más ancho para aprovechar la derecha
      margin: "20px auto",
      padding: "20px",
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      color: "#34495e",
    }}>
      <style>{`
        @keyframes whaleGlowPulse {
          0% { box-shadow: 0 0 0 0 rgba(30,136,229,0.35), 0 0 24px rgba(30,136,229,0.45); }
          50% { box-shadow: 0 0 0 6px rgba(30,136,229,0.12), 0 0 36px rgba(30,136,229,0.65); }
          100% { box-shadow: 0 0 0 0 rgba(30,136,229,0.35), 0 0 24px rgba(30,136,229,0.45); }
        }
        .whale-glow { animation: whaleGlowPulse 1.6s ease-in-out infinite; border-radius: 14px; }
        @media (max-width: 980px) {
          .wc-grid { grid-template-columns: 1fr !important; }
          .wc-sidebar { display: none; }
        }
      `}</style>

      {/* Encabezado */}
      <div style={{
        textAlign: "center", marginBottom: "40px", padding: "20px",
        backgroundColor: "#f7f9fc", borderRadius: "10px",
        boxShadow: "0 4px 10px rgba(0,0,0,0.05)",
      }}>
        <h1 style={{ color: "#1e88e5", fontWeight: 700 }}>Bienvenido a WhaleCorp</h1>
        <p style={{ fontSize: 18, maxWidth: 600, margin: "0 auto", color: "#66788a" }}>
          A page made with love ♥
        </p>
      </div>

      {/* Layout: si no hay LIVE => 1 columna; si hay => 2 columnas */}
      <div
        className="wc-grid"
        style={{
          display: "grid",
          gridTemplateColumns: hasLive ? "minmax(0,1fr) 420px" : "1fr",
          gap: 24,
          alignItems: "start",
        }}
      >
        {/* COLUMNA PRINCIPAL */}
        <main>
          {loading ? (
            <div style={{ textAlign: "center", margin: "50px 0" }}>
              <div className="spinner" />
              <p style={{ color: "#95a5a6", fontSize: 16 }}>Cargando personajes...</p>
            </div>
          ) : error ? (
            <div style={{ textAlign: "center", margin: "50px 0", color: "#e74c3c" }}>
              <p>Error al cargar los datos: {error}</p>
              <button onClick={() => window.location.reload()} style={{
                padding: "10px 20px", background: "#1e88e5", color: "#fff",
                border: "none", borderRadius: 6, cursor: "pointer", fontWeight: 600,
                fontSize: 16, marginTop: 15, boxShadow: "0 2px 6px rgba(30,136,229,0.4)",
                transition: "background-color .3s ease",
              }}
                onMouseOver={(e)=>e.currentTarget.style.backgroundColor="#1565c0"}
                onMouseOut={(e)=>e.currentTarget.style.backgroundColor="#1e88e5"}
              >
                Reintentar
              </button>
            </div>
          ) : visibleCards.length === 0 ? (
            <div style={{ textAlign: "center", margin: "50px 0", color: "#7f8c8d", fontSize: 18 }}>
              <p>No hay tarjetas disponibles</p>
              <Link to="/login" style={{
                display:"inline-block", marginTop:20, padding:"10px 25px",
                background:"#1e88e5", color:"#fff", textDecoration:"none",
                borderRadius:8, fontWeight:600, boxShadow:"0 2px 6px rgba(30,136,229,0.4)",
                transition:"background-color .3s ease",
              }}
                onMouseOver={(e)=>e.currentTarget.style.backgroundColor="#1565c0"}
                onMouseOut={(e)=>e.currentTarget.style.backgroundColor="#1e88e5"}
              >
                Inicia sesión como administrador
              </Link>
            </div>
          ) : (
            <div>
              <h2 style={{ textAlign:"center", marginBottom:30, color:"#2c3e50", fontWeight:700, letterSpacing:"1px" }}>
                Pending Raids
              </h2>

              <div style={{
                display:"grid", gridTemplateColumns:"repeat(3, minmax(0,1fr))",
                gap:24, justifyItems:"stretch",
              }}>
                {visibleCards.map((card, index) => {
                  const status = getCardStatus(card, nowTs);
                  const isLive = status === "live";
                  const team1Raw = Array.isArray(card.team1) ? card.team1
                                  : Array.isArray(card.characters) ? card.characters.slice(0,4) : [];
                  const team2Raw = Array.isArray(card.team2) ? card.team2
                                  : Array.isArray(card.characters) ? card.characters.slice(4,8) : [];
                  const team1 = sortTeam(team1Raw).slice(0,4);
                  const team2 = sortTeam(team2Raw).slice(0,4);
                  const hasAny = team1.length + team2.length > 0;

                  return (
                    <div key={index} className={isLive ? "whale-glow" : undefined}
                      style={{
                        borderRadius:14, overflow:"hidden",
                        boxShadow: isLive
                          ? "0 0 0 0 rgba(30,136,229,0.35), 0 0 24px rgba(30,136,229,0.55)"
                          : "0 10px 20px rgba(0,0,0,0.08)",
                        background:"#fff", display:"flex", flexDirection:"column",
                        cursor:"default", transition:"transform .25s ease, box-shadow .25s ease",
                        width:"100%",
                      }}
                      onMouseEnter={(e)=>{ if(!isLive){ e.currentTarget.style.transform="translateY(-6px)"; e.currentTarget.style.boxShadow="0 20px 40px rgba(0,0,0,0.12)"; }}}
                      onMouseLeave={(e)=>{ if(!isLive){ e.currentTarget.style.transform="translateY(0)"; e.currentTarget.style.boxShadow="0 10px 20px rgba(0,0,0,0.08)"; }}}
                    >
                      <div style={{ padding:"18px 22px", background:"#e3f2fd", borderBottom:"1px solid #bbdefb", userSelect:"none" }}>
                        <h3 style={{
                          margin:0, color:"#1565c0", fontWeight:700, fontSize:"1.3rem",
                          lineHeight:1.15, textOverflow:"ellipsis", whiteSpace:"nowrap", overflow:"hidden",
                        }} title={card.title}>{card.title}</h3>
                        <div style={{ display:"flex", justifyContent:"space-between",
                                      fontSize:14, color:"#607d8b", marginTop:6, fontWeight:600 }}>
                          <span>{card.date ? formatDayMonth(card.date) : "Fecha no asignada"}</span>
                          <span>{card.time || "Hora no asignada"}</span>
                        </div>
                      </div>

                      <div style={{ padding:"18px 22px", display:"grid",
                                    gridTemplateColumns:"1fr 1fr", gap:16, minHeight:"auto" }}>
                        {!hasAny ? (
                          <p style={{ color:"#90a4ae", textAlign:"center", padding:"30px 0",
                                      gridColumn:"1 / -1", fontWeight:500, fontStyle:"italic", margin:0 }}>
                            Tarjeta pendiente de asignación
                          </p>
                        ) : (
                          <>
                            <div>
                              <div style={{ display:"flex", justifyContent:"space-between", alignItems:"center", marginBottom:8 }}>
                                <strong style={{ color:"#0d47a1" }}>Party 1</strong>
                                <span style={{ fontSize:12, color:"#64748b" }}>{team1.length}/4</span>
                              </div>
                              <div style={{ display:"grid", gridTemplateRows:"repeat(4, minmax(0,auto))", gap:10 }}>
                                {team1.map((char,i)=>(<CharItem key={`t1-${i}-${char?.name||i}`} char={char}/>))}
                              </div>
                            </div>
                            <div>
                              <div style={{ display:"flex", justifyContent:"space-between", alignItems:"center", marginBottom:8 }}>
                                <strong style={{ color:"#0d47a1" }}>Party 2</strong>
                                <span style={{ fontSize:12, color:"#64748b" }}>{team2.length}/4</span>
                              </div>
                              <div style={{ display:"grid", gridTemplateRows:"repeat(4, minmax(0,auto))", gap:10 }}>
                                {team2.map((char,i)=>(<CharItem key={`t2-${i}-${char?.name||i}`} char={char}/>))}
                              </div>
                            </div>
                          </>
                        )}
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          )}
        </main>

        {/* SIDEBAR DERECHA: solo se renderiza cuando hay LIVE */}
        {hasLive && (
          <aside className="wc-sidebar" style={{ width: "100%" }}>
            <Streamings
              channels={TWITCH_LINKS}
              pollIntervalMs={60000}
              withTitle
              onLiveChange={(count) => setHasLive(count > 0)}
            />
          </aside>
        )}
      </div>
    </div>
  );
}
