// frontend/src/components/CardSlot.jsx
import React from "react";
import Select from "react-select";

const SPECIAL_TAIL = new Set(["Bard", "Paladin", "Artist", "Valkyrie"]);

// Ordena: primero clases "normales", y al final las del SPECIAL_TAIL (orden estable)
const sortTeam = (arr) => {
  const withIndex = arr.map((c, i) => ({ c, i }));
  withIndex.sort((a, b) => {
    const aIsTail = SPECIAL_TAIL.has(a.c?.class?.name);
    const bIsTail = SPECIAL_TAIL.has(b.c?.class?.name);
    if (aIsTail !== bIsTail) return aIsTail ? 1 : -1;
    return a.i - b.i;
  });
  return withIndex.map((x) => x.c);
};

const CardSlot = ({
  card,
  allCharacters = [],
  onUpdateCardData,
  onDeleteCard,
}) => {
  // Fallback si aún viene "characters" plano desde tarjetas antiguas
  const baseTeam1 = Array.isArray(card.team1)
    ? card.team1
    : Array.isArray(card.characters)
    ? card.characters.slice(0, 4)
    : [];
  const baseTeam2 = Array.isArray(card.team2)
    ? card.team2
    : Array.isArray(card.characters)
    ? card.characters.slice(4, 8)
    : [];

  const team1Sorted = React.useMemo(() => sortTeam(baseTeam1).slice(0, 4), [baseTeam1]);
  const team2Sorted = React.useMemo(() => sortTeam(baseTeam2).slice(0, 4), [baseTeam2]);

  // Opciones del selector (guardamos el OBJETO COMPLETO en value)
  const options = React.useMemo(
    () =>
      allCharacters.map((char) => ({
        value: char,      // objeto completo
        label: char.name, // visible
      })),
    [allCharacters]
  );

  // Valores seleccionados por equipo
  const selectedTeam1 = React.useMemo(
    () => team1Sorted.map((c) => ({ value: c, label: c.name })),
    [team1Sorted]
  );
  const selectedTeam2 = React.useMemo(
    () => team2Sorted.map((c) => ({ value: c, label: c.name })),
    [team2Sorted]
  );

  const handleDateChange = (e) => {
    onUpdateCardData(card.id, { date: e.target.value });
  };

  const handleTimeChange = (e) => {
    onUpdateCardData(card.id, { time: e.target.value });
  };

  // Al cambiar un equipo, limitamos a 4, imponemos el orden con sortTeam
  // y sincronizamos `characters = team1 + team2` para compatibilidad.
  const handleTeamChange = (teamKey, selected) => {
    const raw = (selected || []).map((o) => o.value);
    const limited = raw.slice(0, 4);
    const ordered = sortTeam(limited);

    if (teamKey === "team1") {
      const newTeam1 = ordered;
      const newTeam2 = team2Sorted; // estado actual (ya ordenado y limitado)
      onUpdateCardData(card.id, {
        team1: newTeam1,
        team2: newTeam2,
        characters: [...newTeam1, ...newTeam2],
      });
    } else {
      const newTeam2 = ordered;
      const newTeam1 = team1Sorted;
      onUpdateCardData(card.id, {
        team1: newTeam1,
        team2: newTeam2,
        characters: [...newTeam1, ...newTeam2],
      });
    }
  };

  const Item = ({ character }) => {
    if (!character) return null;
    return (
      <div
        style={{
          background: "#f8fafc",
          border: "1px solid #e2e8f0",
          borderRadius: 10,
          padding: 8,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          gap: 6,
          minHeight: 80,
        }}
        title={character.name}
      >
        {character.class?.image ? (
          <img
            src={character.class.image}
            alt={character.class?.name || "Clase"}
            style={{ width: 32, height: 32, borderRadius: 8, objectFit: "cover" }}
            loading="lazy"
            decoding="async"
          />
        ) : (
          <div
            aria-hidden
            style={{
              width: 32,
              height: 32,
              borderRadius: 8,
              background: "#e5e7eb",
              display: "grid",
              placeItems: "center",
              fontSize: 12,
              color: "#6b7280",
            }}
          >
            ?
          </div>
        )}

        <div
          style={{
            fontWeight: 600,
            fontSize: 13,
            color: "#0f172a",
            textAlign: "center",
            whiteSpace: "nowrap",
            overflow: "hidden",
            textOverflow: "ellipsis",
            width: "100%",
          }}
        >
          {character.name}
        </div>

        {character.class?.name && (
          <div style={{ fontSize: 11, color: "#64748b" }}>{character.class.name}</div>
        )}
      </div>
    );
  };

  return (
    <div
      style={{
        border: `2px dashed #ccc`,
        padding: 15,
        marginBottom: 20,
        borderRadius: 8,
        backgroundColor: "#fff",
        minHeight: 150,
        position: "relative",
      }}
    >
      {/* Header */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h3 style={{ margin: 0 }}>{card.title}</h3>
        <button
          onClick={() => onDeleteCard(card.id)}
          style={{
            background: "#ff4d4d",
            color: "white",
            border: "none",
            borderRadius: 4,
            padding: "5px 10px",
            cursor: "pointer",
          }}
        >
          Eliminar
        </button>
      </div>

      {/* Fecha/Hora */}
      <div style={{ display: "flex", gap: 10, margin: "10px 0" }}>
        <div>
          <label>Fecha: </label>
          <input type="date" value={card.date || ""} onChange={handleDateChange} style={{ padding: 5 }} />
        </div>
        <div>
          <label>Hora: </label>
          <input type="time" value={card.time || ""} onChange={handleTimeChange} style={{ padding: 5 }} />
        </div>
      </div>

      {/* Equipos */}
      <div style={{ marginTop: 10 }}>
        <strong>Personajes por equipo (máx 4 por equipo)</strong>

        {/* Selectores de equipo */}
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "1fr 1fr",
            gap: 16,
            marginTop: 10,
            alignItems: "start",
          }}
        >
          {/* TEAM 1 - Selector */}
          <div>
            <div style={{ display: "flex", justifyContent: "space-between", marginBottom: 6 }}>
              <span style={{ fontWeight: 700, color: "#0f172a" }}>Team 1</span>
              <span style={{ fontSize: 12, color: "#64748b" }}>{team1Sorted.length}/4</span>
            </div>
            <Select
              isMulti
              options={options}
              value={selectedTeam1}
              onChange={(v) => handleTeamChange("team1", v)}
              placeholder="Selecciona personajes..."
              closeMenuOnSelect={false}
              styles={{
                control: (p) => ({ ...p, borderRadius: 8, minHeight: 40, fontSize: "0.95rem" }),
                multiValue: (p) => ({ ...p, backgroundColor: "#bbdefb", borderRadius: 20 }),
                multiValueLabel: (p) => ({ ...p, color: "#0d47a1", fontWeight: 600 }),
                multiValueRemove: (p) => ({
                  ...p,
                  color: "#0d47a1",
                  ":hover": { backgroundColor: "#0d47a1", color: "white" },
                }),
              }}
            />

            {/* Vista del equipo 1 (ícono arriba, nombre abajo) */}
            <div style={{ display: "grid", gridTemplateRows: "repeat(4, minmax(0, auto))", gap: 10, marginTop: 10 }}>
              {team1Sorted.map((character, idx) => (
                <Item key={`team1-${idx}-${character?.name || idx}`} character={character} />
              ))}
            </div>
          </div>

          {/* TEAM 2 - Selector */}
          <div>
            <div style={{ display: "flex", justifyContent: "space-between", marginBottom: 6 }}>
              <span style={{ fontWeight: 700, color: "#0f172a" }}>Team 2</span>
              <span style={{ fontSize: 12, color: "#64748b" }}>{team2Sorted.length}/4</span>
            </div>
            <Select
              isMulti
              options={options}
              value={selectedTeam2}
              onChange={(v) => handleTeamChange("team2", v)}
              placeholder="Selecciona personajes..."
              closeMenuOnSelect={false}
              styles={{
                control: (p) => ({ ...p, borderRadius: 8, minHeight: 40, fontSize: "0.95rem" }),
                multiValue: (p) => ({ ...p, backgroundColor: "#bbdefb", borderRadius: 20 }),
                multiValueLabel: (p) => ({ ...p, color: "#0d47a1", fontWeight: 600 }),
                multiValueRemove: (p) => ({
                  ...p,
                  color: "#0d47a1",
                  ":hover": { backgroundColor: "#0d47a1", color: "white" },
                }),
              }}
            />

            {/* Vista del equipo 2 (ícono arriba, nombre abajo) */}
            <div style={{ display: "grid", gridTemplateRows: "repeat(4, minmax(0, auto))", gap: 10, marginTop: 10 }}>
              {team2Sorted.map((character, idx) => (
                <Item key={`team2-${idx}-${character?.name || idx}`} character={character} />
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CardSlot;
