// frontend/src/components/AdminPanel.jsx
import React, { useState, useMemo } from "react";
import Modal from "react-modal";
import Select from "react-select";
import MiscellaneumClasses from "./admin/MiscellaneumClasses";

Modal.setAppElement("#root");

const SPECIAL_TAIL = new Set(["Bard", "Paladin", "Artist", "Valkyrie"]);
const sortTeam = (arr) => {
  const withIndex = arr.map((c, i) => ({ c, i }));
  withIndex.sort((a, b) => {
    const aIsTail = SPECIAL_TAIL.has(a.c?.class?.name);
    const bIsTail = SPECIAL_TAIL.has(b.c?.class?.name);
    if (aIsTail !== bIsTail) return aIsTail ? 1 : -1; // tail al final
    return a.i - b.i; // estable dentro del grupo
  });
  return withIndex.map((x) => x.c);
};

// ---- Helpers de fecha MM-DD (sin año) ----
const mmddRegex = /^(0[1-9]|1[0-2])-(0[1-9]|[12]\d|3[01])$/;

const toMMDD = (s) => {
  if (!s || typeof s !== "string") return "";
  const t = s.trim();
  if (mmddRegex.test(t)) return t;
  // Soporta MM/DD
  if (/^(0[1-9]|1[0-2])\/(0[1-9]|[12]\d|3[01])$/.test(t)) {
    const [m, d] = t.split("/");
    return `${m}-${d}`;
  }
  // Soporta YYYY-MM-DD
  const ymd = t.match(/^(\d{4})-(0[1-9]|1[0-2])-(0[1-9]|[12]\d|3[01])$/);
  if (ymd) {
    const [, , m, d] = ymd;
    return `${m}-${d}`;
  }
  return t; // deja pasar para que el validador al guardar avise si no es válido
};

const isValidMMDD = (mmdd) => {
  if (!mmddRegex.test(mmdd)) return false;
  // Validación de días por mes usando un año fijo no bisiesto (2001)
  const [m, d] = mmdd.split("-").map((x) => parseInt(x, 10));
  const date = new Date(2001, m - 1, d);
  return date.getMonth() + 1 === m && date.getDate() === d;
};

const AdminPanel = ({ cards, allCharacters, onSaveCards }) => {
  // Header: nuevo título para crear tarjeta
  const [createTitle, setCreateTitle] = useState("");

  // Modal state
  const [modalOpen, setModalOpen] = useState(false);
  const [editingCardId, setEditingCardId] = useState(null);

  // Campos del modal
  const [modalTitle, setModalTitle] = useState("");
  const [selectedDate, setSelectedDate] = useState(""); // ahora MM-DD
  const [selectedTime, setSelectedTime] = useState("");

  // Select por equipo
  const [selectedTeam1, setSelectedTeam1] = useState([]); // [{value: char, label: char.name}]
  const [selectedTeam2, setSelectedTeam2] = useState([]);

  // Opciones: objeto personaje completo en value
  const options = useMemo(
    () =>
      (allCharacters ?? []).map((char) => ({
        value: char,
        label: char.name,
      })),
    [allCharacters]
  );

  // Helpers para cargar valores del modal desde una card
  const loadTeamsFromCard = (card) => {
    const fallbackChars = Array.isArray(card?.characters) ? card.characters : [];
    const t1 = Array.isArray(card?.team1) ? card.team1 : fallbackChars.slice(0, 4);
    const t2 = Array.isArray(card?.team2) ? card.team2 : fallbackChars.slice(4, 8);

    const t1Sorted = sortTeam(t1).slice(0, 4);
    const t2Sorted = sortTeam(t2).slice(0, 4);

    setSelectedTeam1(t1Sorted.map((c) => ({ value: c, label: c.name })));
    setSelectedTeam2(t2Sorted.map((c) => ({ value: c, label: c.name })));
  };

  // Abrir modal (crear/editar)
  const openModal = (card) => {
    setEditingCardId(card?.id || null);
    setModalTitle(card?.title || createTitle || "");
    // Normaliza a MM-DD si viene YYYY-MM-DD o MM/DD
    setSelectedDate(toMMDD(card?.date || ""));
    setSelectedTime(card?.time || "");
    loadTeamsFromCard(card);
    setModalOpen(true);
  };

  // Guardar modal -> valida y persiste team1/team2 + characters
  const saveModal = () => {
    const trimmedTitle = (modalTitle || createTitle || "").trim();
    if (!trimmedTitle) {
      alert("Debe ingresar un título para la tarjeta");
      return;
    }

    // Validar MM-DD
    const normalized = toMMDD(selectedDate);
    if (normalized && !isValidMMDD(normalized)) {
      alert("La fecha debe estar en formato MM-DD y ser válida. Ej: 08-16");
      return;
    }

    const team1 = sortTeam((selectedTeam1 || []).map((o) => o.value)).slice(0, 4);
    const team2 = sortTeam((selectedTeam2 || []).map((o) => o.value)).slice(0, 4);

    if (team1.length + team2.length === 0) {
      alert("Debe seleccionar al menos un personaje (en team1 o team2)");
      return;
    }

    const newCardData = {
      title: trimmedTitle,
      date: normalized, // guarda sólo MM-DD (sin año)
      time: selectedTime,
      team1,
      team2,
      // compatibilidad: characters (team1 + team2)
      characters: [...team1, ...team2],
    };

    if (editingCardId) {
      const updated = cards.map((c) => (c.id === editingCardId ? { ...c, ...newCardData } : c));
      onSaveCards(updated);
    } else {
      const newCard = { id: Date.now(), ...newCardData };
      onSaveCards([...(cards || []), newCard]);
      setCreateTitle("");
    }

    setModalOpen(false);
  };

  // Eliminar tarjeta
  const deleteCard = (cardId) => {
    if (window.confirm("¿Seguro que quieres eliminar esta tarjeta?")) {
      onSaveCards(cards.filter((c) => c.id !== cardId));
    }
  };

  // Handlers de selects
  const onTeamChange = (teamKey, value) => {
    const list = (value || []).slice(0, 4); // no permitir más de 4 en UI
    if (teamKey === "team1") setSelectedTeam1(list);
    else setSelectedTeam2(list);
  };

  // Handler de entrada para permitir solo dígitos y guion, y autoformateo simple
  const handleDateInput = (e) => {
    let v = e.target.value.replace(/[^\d\-\/]/g, "");
    // Sustituye / por -
    v = v.replace(/\//g, "-");
    // Evita más de 5 chars "MM-DD"
    if (v.length > 5) v = v.slice(0, 5);
    // Inserta guion después de 2 dígitos si el usuario escribe "MMDD"
    if (/^\d{3,4}$/.test(v) && !v.includes("-")) {
      v = `${v.slice(0, 2)}-${v.slice(2)}`;
    }
    setSelectedDate(v);
  };

  return (
    <div
      style={{
        padding: 20,
        maxWidth: 1100,
        margin: "0 auto",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        color: "#34495e",
        position: "relative",
        minHeight: "100vh",
      }}
    >
      <h1 style={{ marginBottom: 20, color: "#1e88e5", fontWeight: "700" }}>Panel de Administración</h1>

      {/* Header creación */}
      <div style={{ marginBottom: 20, display: "flex", gap: 10 }}>
        <input
          type="text"
          placeholder="Título para nueva tarjeta"
          value={createTitle}
          onChange={(e) => setCreateTitle(e.target.value)}
          style={{
            flex: 1,
            padding: 10,
            borderRadius: 8,
            border: "1px solid #ccc",
            fontSize: "1rem",
            outline: "none",
          }}
        />
        <button
          onClick={() => openModal(null)}
          style={{
            padding: "10px 20px",
            cursor: "pointer",
            backgroundColor: "#1e88e5",
            color: "white",
            border: "none",
            borderRadius: 8,
            fontWeight: "600",
            fontSize: "1rem",
            boxShadow: "0 3px 6px rgba(30,136,229,0.5)",
            transition: "background-color 0.3s ease",
          }}
          onMouseOver={(e) => (e.currentTarget.style.backgroundColor = "#1565c0")}
          onMouseOut={(e) => (e.currentTarget.style.backgroundColor = "#1e88e5")}
        >
          Crear Tarjeta
        </button>
      </div>

      {/* Grid de tarjetas -> 3 columnas */}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(3, 1fr)",
          gap: 24,
          justifyItems: "stretch",
        }}
      >
        {(!cards || cards.length === 0) ? (
          <p>No hay tarjetas creadas</p>
        ) : (
          cards.map((card) => (
            <div
              key={card.id}
              style={{
                borderRadius: 12,
                boxShadow: "0 12px 20px rgba(0,0,0,0.08)",
                backgroundColor: "#f0f5ff",
                display: "flex",
                flexDirection: "column",
                padding: 12,
                justifyContent: "space-between",
                cursor: "default",
                width: "100%",
              }}
            >
              <div>
                <h3
                  style={{
                    color: "#1e88e5",
                    fontWeight: "700",
                    marginBottom: 6,
                    whiteSpace: "nowrap",
                    overflow: "hidden",
                    textOverflow: "ellipsis",
                  }}
                  title={card.title}
                >
                  {card.title}
                </h3>
                <p style={{ color: "#607d8b", fontWeight: "600", fontSize: "0.9rem", margin: 0 }}>
                  {toMMDD(card.date) || "No asignada"}
                </p>
                <p style={{ color: "#607d8b", fontWeight: "600", fontSize: "0.9rem", marginTop: 2 }}>
                  {card.time || "No asignada"}
                </p>

                {/* Chips de personajes (compat chars) */}
                <div style={{ marginTop: 10 }}>
                  <strong style={{ fontWeight: "700", color: "#0d47a1" }}>Personajes:</strong>
                  <div style={{ display: "flex", flexWrap: "wrap", gap: 6, marginTop: 6 }}>
                    {(card.characters ?? []).map((c, i) => (
                      <span
                        key={i}
                        style={{
                          display: "inline-flex",
                          alignItems: "center",
                          gap: 6,
                          backgroundColor: "#bbdefb",
                          padding: "5px 10px",
                          borderRadius: 20,
                          fontSize: "0.9em",
                          fontWeight: 600,
                          color: "#0d47a1",
                          userSelect: "none",
                          boxShadow: "0 1px 4px rgba(0,71,173,0.3)",
                        }}
                      >
                        {c.class?.image && (
                          <img
                            src={c.class.image}
                            alt={c.class?.name || "Clase"}
                            style={{ width: 18, height: 18, borderRadius: "50%" }}
                          />
                        )}
                        {c.name}
                      </span>
                    ))}
                  </div>
                </div>
              </div>

              <div style={{ marginTop: 14, display: "flex", gap: 8 }}>
                <button
                  onClick={() => openModal(card)}
                  style={{
                    flex: 1,
                    padding: "8px 0",
                    cursor: "pointer",
                    borderRadius: 8,
                    border: "none",
                    backgroundColor: "#1976d2",
                    color: "white",
                    fontWeight: "600",
                    transition: "background-color 0.3s ease",
                  }}
                  onMouseOver={(e) => (e.currentTarget.style.backgroundColor = "#115293")}
                  onMouseOut={(e) => (e.currentTarget.style.backgroundColor = "#1976d2")}
                >
                  Editar Tarjeta
                </button>

                <button
                  onClick={() => deleteCard(card.id)}
                  style={{
                    flex: 1,
                    padding: "8px 0",
                    cursor: "pointer",
                    borderRadius: 8,
                    border: "none",
                    backgroundColor: "#d32f2f",
                    color: "white",
                    fontWeight: "600",
                    transition: "background-color 0.3s ease",
                  }}
                  onMouseOver={(e) => (e.currentTarget.style.backgroundColor = "#9a2424")}
                  onMouseOut={(e) => (e.currentTarget.style.backgroundColor = "#d32f2f")}
                >
                  Eliminar Tarjeta
                </button>
              </div>
            </div>
          ))
        )}
      </div>

      {/* MODAL */}
      <Modal
        isOpen={modalOpen}
        onRequestClose={() => setModalOpen(false)}
        contentLabel="Configurar Tarjeta"
        style={{
          content: {
            maxWidth: "720px",
            margin: "auto",
            height: "fit-content",
            padding: "20px",
            borderRadius: "12px",
            boxShadow: "0 10px 30px rgba(0,0,0,0.15)",
            zIndex: 1000,
          },
          overlay: { zIndex: 999 },
        }}
      >
        <h2 style={{ marginBottom: 16 }}>{editingCardId ? "Editar Tarjeta" : "Nueva Tarjeta"}</h2>

        {/* Título */}
        <input
          type="text"
          placeholder="Título para la tarjeta"
          value={modalTitle}
          onChange={(e) => setModalTitle(e.target.value)}
          style={{
            width: "100%",
            padding: 12,
            marginBottom: 16,
            borderRadius: 8,
            border: "1px solid #ccc",
            fontSize: "1rem",
            outline: "none",
          }}
        />

        {/* Fecha (MM-DD) / Hora */}
        <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 12, marginBottom: 16 }}>
          <label style={{ display: "block" }}>
            Fecha (MM-DD):
            <input
              type="text"
              inputMode="numeric"
              placeholder="MM-DD"
              value={selectedDate}
              onChange={handleDateInput}
              onBlur={() => setSelectedDate(toMMDD(selectedDate))}
              style={{
                width: "100%",
                padding: 10,
                marginTop: 6,
                borderRadius: 8,
                border: "1px solid #ccc",
                fontSize: "1rem",
                letterSpacing: "0.5px",
              }}
            />
          </label>

          <label style={{ display: "block" }}>
            Hora:
            <input
              type="time"
              value={selectedTime}
              onChange={(e) => setSelectedTime(e.target.value)}
              style={{
                width: "100%",
                padding: 10,
                marginTop: 6,
                borderRadius: 8,
                border: "1px solid #ccc",
                fontSize: "1rem",
              }}
            />
          </label>
        </div>

        {/* Equipos lado a lado */}
        <div style={{ fontWeight: 600, marginBottom: 8 }}>Selecciona personajes por equipo (máx 4 por equipo):</div>

        <div
          style={{
            display: "grid",
            gridTemplateColumns: "1fr 1fr",
            gap: 16,
            alignItems: "start",
          }}
        >
          {/* TEAM 1 */}
          <div>
            <div style={{ display: "flex", justifyContent: "space-between", marginBottom: 6 }}>
              <span style={{ fontWeight: 700, color: "#0f172a" }}>Team 1</span>
              <span style={{ fontSize: 12, color: "#64748b" }}>{selectedTeam1.length}/4</span>
            </div>
            <Select
              isMulti
              options={options}
              value={selectedTeam1}
              onChange={(v) => onTeamChange("team1", v)}
              placeholder="Buscar personajes..."
              closeMenuOnSelect={false}
              styles={{
                control: (p) => ({ ...p, borderRadius: 8, minHeight: 40, fontSize: "0.95rem" }),
                multiValue: (p) => ({ ...p, backgroundColor: "#bbdefb", borderRadius: 20 }),
                multiValueLabel: (p) => ({ ...p, color: "#0d47a1", fontWeight: 600 }),
                multiValueRemove: (p) => ({
                  ...p,
                  color: "#0d47a1",
                  ":hover": { backgroundColor: "#0d47a1", color: "white" },
                }),
              }}
            />
            {/* vista rápida de slots (opcional) */}
            <div style={{ display: "grid", gridTemplateRows: "repeat(4, minmax(0, auto))", gap: 8, marginTop: 10 }}>
              {sortTeam(selectedTeam1.map((x) => x.value)).slice(0, 4).map((c, i) => (
                <div
                  key={`t1-${i}-${c?.name || i}`}
                  style={{
                    background: "#f8fafc",
                    border: "1px solid #e2e8f0",
                    borderRadius: 10,
                    padding: 8,
                    display: "flex",
                    alignItems: "center",
                    gap: 8,
                  }}
                >
                  {c?.class?.image && (
                    <img src={c.class.image} alt={c.class.name} style={{ width: 20, height: 20, borderRadius: 6 }} />
                  )}
                  <div style={{ fontSize: 13, fontWeight: 600 }}>{c?.name}</div>
                  <div style={{ fontSize: 11, color: "#64748b", marginLeft: "auto" }}>{c?.class?.name}</div>
                </div>
              ))}
            </div>
          </div>

          {/* TEAM 2 */}
          <div>
            <div style={{ display: "flex", justifyContent: "space-between", marginBottom: 6 }}>
              <span style={{ fontWeight: 700, color: "#0f172a" }}>Team 2</span>
              <span style={{ fontSize: 12, color: "#64748b" }}>{selectedTeam2.length}/4</span>
            </div>
            <Select
              isMulti
              options={options}
              value={selectedTeam2}
              onChange={(v) => onTeamChange("team2", v)}
              placeholder="Buscar personajes..."
              closeMenuOnSelect={false}
              styles={{
                control: (p) => ({ ...p, borderRadius: 8, minHeight: 40, fontSize: "0.95rem" }),
                multiValue: (p) => ({ ...p, backgroundColor: "#bbdefb", borderRadius: 20 }),
                multiValueLabel: (p) => ({ ...p, color: "#0d47a1", fontWeight: 600 }),
                multiValueRemove: (p) => ({
                  ...p,
                  color: "#0d47a1",
                  ":hover": { backgroundColor: "#0d47a1", color: "white" },
                }),
              }}
            />
            <div style={{ display: "grid", gridTemplateRows: "repeat(4, minmax(0, auto))", gap: 8, marginTop: 10 }}>
              {sortTeam(selectedTeam2.map((x) => x.value)).slice(0, 4).map((c, i) => (
                <div
                  key={`t2-${i}-${c?.name || i}`}
                  style={{
                    background: "#f8fafc",
                    border: "1px solid #e2e8f0",
                    borderRadius: 10,
                    padding: 8,
                    display: "flex",
                    alignItems: "center",
                    gap: 8,
                  }}
                >
                  {c?.class?.image && (
                    <img src={c.class.image} alt={c.class.name} style={{ width: 20, height: 20, borderRadius: 6 }} />
                  )}
                  <div style={{ fontSize: 13, fontWeight: 600 }}>{c?.name}</div>
                  <div style={{ fontSize: 11, color: "#64748b", marginLeft: "auto" }}>{c?.class?.name}</div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Footer acciones */}
        <div style={{ marginTop: 20, display: "flex", justifyContent: "flex-end", gap: 12 }}>
          <button
            onClick={() => setModalOpen(false)}
            style={{
              padding: "10px 20px",
              borderRadius: 8,
              border: "1px solid #ccc",
              backgroundColor: "white",
              cursor: "pointer",
              fontWeight: "600",
              fontSize: "1rem",
            }}
          >
            Cancelar
          </button>
          <button
            onClick={saveModal}
            style={{
              padding: "10px 20px",
              borderRadius: 8,
              border: "none",
              backgroundColor: "#1e88e5",
              color: "white",
              cursor: "pointer",
              fontWeight: "600",
              fontSize: "1rem",
              boxShadow: "0 3px 6px rgba(30,136,229,0.5)",
              transition: "background-color 0.3s ease",
            }}
            onMouseOver={(e) => (e.currentTarget.style.backgroundColor = "#1565c0")}
            onMouseOut={(e) => (e.currentTarget.style.backgroundColor = "#1e88e5")}
          >
            Guardar
          </button>
        </div>
      </Modal>

      {/* Miscellaneum fijo (min/max) */}
      <MiscellaneumClasses title="Clases" sortMode="asIs" />
    </div>
  );
};

export default AdminPanel;
