// frontend/src/components/HomePage.jsx
import React from "react";
import { Link } from "react-router-dom";

// Normalizamos para que siempre caigan al final sin importar mayúsculas/espacios
const SPECIAL_TAIL = new Set(["bard", "paladin", "artist", "valkyrie"]);
const isTailClass = (clsName) =>
  typeof clsName === "string" && SPECIAL_TAIL.has(clsName.trim().toLowerCase());

// Ordena: primero NO especiales, al final especiales (orden estable)
const sortTeam = (arr = []) => {
  const withIndex = arr.map((c, i) => ({ c, i }));
  withIndex.sort((a, b) => {
    const aIsTail = isTailClass(a.c?.class?.name);
    const bIsTail = isTailClass(b.c?.class?.name);
    if (aIsTail !== bIsTail) return aIsTail ? 1 : -1; // especiales al final
    return a.i - b.i; // orden estable dentro del grupo
  });
  return withIndex.map((x) => x.c);
};

// Formatea YYYY-MM-DD a DD-MM (solo día y mes). Si no es parseable, intenta split.
const formatDayMonth = (iso) => {
  if (!iso) return "Fecha no asignada";
  const d = new Date(iso);
  if (!isNaN(d)) {
    const dd = String(d.getDate()).padStart(2, "0");
    const mm = String(d.getMonth() + 1).padStart(2, "0");
    return `${mm}-${mm}`;
  }
  // Fallback por si viene como string simple "YYYY-MM-DD"
  const parts = String(iso).split("-");
  if (parts.length === 3) {
    const [y, m, day] = parts;
    if (day && m) return `${day.padStart(2, "0")}-${m.padStart(2, "0")}`;
  }
  return iso;
};

const CharItem = ({ char }) => {
  if (!char) return null;
  return (
    <div
      style={{
        padding: "10px",
        borderRadius: "10px",
        backgroundColor: "#f8fafc",
        border: "1px solid #e2e8f0",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        gap: 6,
        textAlign: "center",
      }}
      title={char.name}
    >
      {char.class?.image ? (
        <img
          src={char.class.image}
          alt={char.class?.name || "Clase"}
          style={{ width: 32, height: 32, borderRadius: 8, objectFit: "cover" }}
          loading="lazy"
          decoding="async"
        />
      ) : (
        <div
          aria-hidden
          style={{
            width: 32,
            height: 32,
            borderRadius: 8,
            background: "#e5e7eb",
            display: "grid",
            placeItems: "center",
            fontSize: 12,
            color: "#6b7280",
          }}
        >
          ?
        </div>
      )}

      <div
        style={{
          fontWeight: 700,
          fontSize: 13,
          color: "#0f172a",
          whiteSpace: "nowrap",
          overflow: "hidden",
          textOverflow: "ellipsis",
          width: "100%",
        }}
      >
        {char.name}
      </div>

      {/* ⬇️ Quitado: nombre de la clase */}
      {/* {char.class?.name && (
        <div style={{ fontSize: 11, color: "#64748b" }}>{char.class.name}</div>
      )} */}
    </div>
  );
};

export default function HomePage({ cards = [], loading, error }) {
  return (
    <div
      style={{
        maxWidth: "1100px",
        margin: "20px auto",
        padding: "20px",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        color: "#34495e",
      }}
    >
      <div
        style={{
          textAlign: "center",
          marginBottom: "40px",
          padding: "20px",
          backgroundColor: "#f7f9fc",
          borderRadius: "10px",
          boxShadow: "0 4px 10px rgba(0,0,0,0.05)",
        }}
      >
        <h1 style={{ color: "#1e88e5", fontWeight: "700" }}>
          Bienvenido a WhaleCorp
        </h1>
        <p
          style={{
            fontSize: "18px",
            maxWidth: "600px",
            margin: "0 auto",
            color: "#66788a",
          }}
        >
          A page made with love ♥
        </p>
      </div>

      {loading ? (
        <div style={{ textAlign: "center", margin: "50px 0" }}>
          <div className="spinner" />
          <p style={{ color: "#95a5a6", fontSize: "16px" }}>
            Cargando personajes...
          </p>
        </div>
      ) : error ? (
        <div
          style={{ textAlign: "center", margin: "50px 0", color: "#e74c3c" }}
        >
          <p>Error al cargar los datos: {error}</p>
          <button
            onClick={() => window.location.reload()}
            style={{
              padding: "10px 20px",
              background: "#1e88e5",
              color: "white",
              border: "none",
              borderRadius: "6px",
              cursor: "pointer",
              fontWeight: "600",
              fontSize: "16px",
              marginTop: "15px",
              boxShadow: "0 2px 6px rgba(30,136,229,0.4)",
              transition: "background-color 0.3s ease",
            }}
            onMouseOver={(e) =>
              (e.currentTarget.style.backgroundColor = "#1565c0")
            }
            onMouseOut={(e) =>
              (e.currentTarget.style.backgroundColor = "#1e88e5")
            }
          >
            Reintentar
          </button>
        </div>
      ) : cards.length === 0 ? (
        <div
          style={{
            textAlign: "center",
            margin: "50px 0",
            color: "#7f8c8d",
            fontSize: "18px",
          }}
        >
          <p>No hay tarjetas disponibles</p>
          <Link
            to="/login"
            style={{
              display: "inline-block",
              marginTop: "20px",
              padding: "10px 25px",
              background: "#1e88e5",
              color: "white",
              textDecoration: "none",
              borderRadius: "8px",
              fontWeight: "600",
              boxShadow: "0 2px 6px rgba(30,136,229,0.4)",
              transition: "background-color 0.3s ease",
            }}
            onMouseOver={(e) =>
              (e.currentTarget.style.backgroundColor = "#1565c0")
            }
            onMouseOut={(e) =>
              (e.currentTarget.style.backgroundColor = "#1e88e5")
            }
          >
            Inicia sesión como administrador
          </Link>
        </div>
      ) : (
        <div>
          <h2
            style={{
              textAlign: "center",
              marginBottom: "30px",
              color: "#2c3e50",
              fontWeight: "700",
              letterSpacing: "1px",
            }}
          >
            Pending Raids
          </h2>

          {/* Grid 3 columnas fijas */}
          <div
            style={{
              display: "grid",
              gridTemplateColumns: "repeat(3, 1fr)",
              gap: "24px",
              justifyItems: "stretch",
            }}
          >
            {cards.map((card, index) => {
              // Compat: si no trae team1/team2, partimos characters en 4 y 4
              const team1Raw = Array.isArray(card.team1)
                ? card.team1
                : Array.isArray(card.characters)
                ? card.characters.slice(0, 4)
                : [];
              const team2Raw = Array.isArray(card.team2)
                ? card.team2
                : Array.isArray(card.characters)
                ? card.characters.slice(4, 8)
                : [];

              const team1 = sortTeam(team1Raw).slice(0, 4);
              const team2 = sortTeam(team2Raw).slice(0, 4);
              const hasAny = team1.length + team2.length > 0;

              return (
                <div
                  key={index}
                  style={{
                    borderRadius: "14px",
                    overflow: "hidden",
                    boxShadow: "0 10px 20px rgba(0,0,0,0.08)",
                    backgroundColor: "#ffffff",
                    display: "flex",
                    flexDirection: "column",
                    cursor: "default",
                    transition: "transform 0.25s ease, box-shadow 0.25s ease",
                    width: "100%",
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.transform = "translateY(-6px)";
                    e.currentTarget.style.boxShadow =
                      "0 20px 40px rgba(0,0,0,0.12)";
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.transform = "translateY(0)";
                    e.currentTarget.style.boxShadow =
                      "0 10px 20px rgba(0,0,0,0.08)";
                  }}
                >
                  {/* Header de tarjeta */}
                  <div
                    style={{
                      padding: "18px 22px",
                      backgroundColor: "#e3f2fd",
                      borderBottom: "1px solid #bbdefb",
                      userSelect: "none",
                    }}
                  >
                    <h3
                      style={{
                        margin: 0,
                        color: "#1565c0",
                        fontWeight: "700",
                        fontSize: "1.3rem",
                        lineHeight: "1.15",
                        textOverflow: "ellipsis",
                        whiteSpace: "nowrap",
                        overflow: "hidden",
                      }}
                      title={card.title}
                    >
                      {card.title}
                    </h3>
                    <div
                      style={{
                        display: "flex",
                        justifyContent: "space-between",
                        fontSize: "14px",
                        color: "#607d8b",
                        marginTop: "6px",
                        fontWeight: "600",
                      }}
                    >
                      {/* 🔵 Solo día-mes */}
                      <span>{card.date ? formatDayMonth(card.date) : "Fecha no asignada"}</span>
                      <span>{card.time || "Hora no asignada"}</span>
                    </div>
                  </div>

                  {/* Cuerpo por equipos: 2 columnas (Team 1 | Team 2) */}
                  <div
                    style={{
                      padding: "18px 22px",
                      display: "grid",
                      gridTemplateColumns: "1fr 1fr",
                      gap: "16px",
                      minHeight: "auto",
                    }}
                  >
                    {!hasAny ? (
                      <p
                        style={{
                          color: "#90a4ae",
                          textAlign: "center",
                          padding: "30px 0",
                          gridColumn: "1 / -1",
                          fontWeight: "500",
                          fontStyle: "italic",
                          margin: 0,
                        }}
                      >
                        Tarjeta pendiente de asignación
                      </p>
                    ) : (
                      <>
                        {/* TEAM 1 */}
                        <div>
                          <div
                            style={{
                              display: "flex",
                              justifyContent: "space-between",
                              alignItems: "center",
                              marginBottom: 8,
                            }}
                          >
                            <strong style={{ color: "#0d47a1" }}>Team 1</strong>
                            <span style={{ fontSize: 12, color: "#64748b" }}>
                              {team1.length}/4
                            </span>
                          </div>
                          <div
                            style={{
                              display: "grid",
                              gridTemplateRows: "repeat(4, minmax(0, auto))",
                              gap: 10,
                            }}
                          >
                            {team1.map((char, i) => (
                              <CharItem key={`t1-${i}-${char?.name || i}`} char={char} />
                            ))}
                          </div>
                        </div>

                        {/* TEAM 2 */}
                        <div>
                          <div
                            style={{
                              display: "flex",
                              justifyContent: "space-between",
                              alignItems: "center",
                              marginBottom: 8,
                            }}
                          >
                            <strong style={{ color: "#0d47a1" }}>Team 2</strong>
                            <span style={{ fontSize: 12, color: "#64748b" }}>
                              {team2.length}/4
                            </span>
                          </div>
                          <div
                            style={{
                              display: "grid",
                              gridTemplateRows: "repeat(4, minmax(0, auto))",
                              gap: 10,
                            }}
                          >
                            {team2.map((char, i) => (
                              <CharItem key={`t2-${i}-${char?.name || i}`} char={char} />
                            ))}
                          </div>
                        </div>
                      </>
                    )}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
}
