import React, { useState, useMemo, useCallback } from "react";
import { DndProvider, useDrag, useDrop } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import { TouchBackend } from "react-dnd-touch-backend";
import { isMobile } from "react-device-detect";

// Componente para elementos arrastrables
const DraggableCharacter = ({ character, onAssign }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: "character",
    item: character,
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  return (
    <div
      ref={drag}
      style={{
        opacity: isDragging ? 0.5 : 1,
        padding: "10px",
        margin: "5px 0",
        border: "1px solid #ddd",
        borderRadius: "4px",
        backgroundColor: "#f9f9f9",
        cursor: "grab",
      }}
    >
      <strong>{character.name}</strong> - {character.label}
    </div>
  );
};

// Componente para tarjetas donde se soltarán los personajes
const CardSlot = ({ card, onUpdateCard, onDeleteCard }) => {
  const [{ isOver }, drop] = useDrop(() => ({
    accept: "character",
    drop: (item) => onUpdateCard(card.id, { ...card, character: item }),
    collect: (monitor) => ({
      isOver: !!monitor.isOver(),
    }),
  }));

  return (
    <div
      ref={drop}
      style={{
        border: `2px dashed ${isOver ? "#4CAF50" : "#ccc"}`,
        padding: "15px",
        marginBottom: "20px",
        borderRadius: "8px",
        backgroundColor: isOver ? "#f0fff0" : "#fff",
        minHeight: "150px",
        position: "relative",
      }}
    >
      <div style={{ display: "flex", justifyContent: "space-between" }}>
        <h3>{card.character ? card.character.name : "Tarjeta vacía"}</h3>
        <button
          onClick={() => onDeleteCard(card.id)}
          style={{
            background: "#ff4d4d",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer",
          }}
        >
          Eliminar
        </button>
      </div>

      {card.character && (
        <div>
          <p><strong>Etiqueta:</strong> {card.character.label}</p>
          <div style={{ display: "flex", gap: "10px", marginTop: "10px" }}>
            <div>
              <label>Fecha:</label>
              <input
                type="date"
                value={card.date || ""}
                onChange={(e) => onUpdateCard(card.id, { ...card, date: e.target.value })}
                style={{ padding: "5px" }}
              />
            </div>
            <div>
              <label>Hora:</label>
              <input
                type="time"
                value={card.time || ""}
                onChange={(e) => onUpdateCard(card.id, { ...card, time: e.target.value })}
                style={{ padding: "5px" }}
              />
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// Componente principal del panel de administrador
const AdminPanel = ({ cards, allCharacters, onSaveCards }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [newCardTitle, setNewCardTitle] = useState("");

  // Filtrar personajes
  const filteredCharacters = useMemo(() => {
    if (!searchTerm) return allCharacters;
    return allCharacters.filter(
      (char) =>
        char.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        char.label.toLowerCase().includes(searchTerm.toLowerCase())
    );
  }, [allCharacters, searchTerm]);

  // Actualizar una tarjeta
  const updateCard = (cardId, updatedCard) => {
    const updatedCards = cards.map((card) =>
      card.id === cardId ? updatedCard : card
    );
    onSaveCards(updatedCards);
  };

  // Eliminar una tarjeta
  const deleteCard = (cardId) => {
    const updatedCards = cards.filter((card) => card.id !== cardId);
    onSaveCards(updatedCards);
  };

  // Agregar nueva tarjeta
  const addNewCard = () => {
    if (!newCardTitle.trim()) return;
    
    const newCard = {
      id: Date.now(),
      title: newCardTitle.trim(),
      character: null,
      date: "",
      time: "",
    };
    
    onSaveCards([...cards, newCard]);
    setNewCardTitle("");
  };

  // Publicar todas las tarjetas
  const publishCards = () => {
    // Validar que todas las tarjetas tengan fecha y hora
    const incompleteCards = cards.filter(
      (card) => !card.character || !card.date || !card.time
    );
    
    if (incompleteCards.length > 0) {
      alert("Algunas tarjetas están incompletas. Completa fecha y hora para todas.");
      return;
    }
    
    alert("¡Tarjetas publicadas con éxito!");
  };

  return (
    <div style={{ padding: "20px", maxWidth: "1200px", margin: "0 auto" }}>
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h1>Panel de Administrador</h1>
        <button
          onClick={publishCards}
          style={{
            padding: "10px 20px",
            background: "#4CAF50",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer",
            fontSize: "16px",
          }}
        >
          Publicar Tarjetas
        </button>
      </div>

      <div style={{ display: "flex", gap: "20px", marginTop: "20px" }}>
        {/* Sección izquierda: Lista de personajes */}
        <div style={{ flex: 1 }}>
          <h2>Personajes Disponibles</h2>
          <input
            type="text"
            placeholder="Buscar personajes..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            style={{
              width: "100%",
              padding: "10px",
              marginBottom: "15px",
              border: "1px solid #ddd",
              borderRadius: "4px",
            }}
          />
          
          <div style={{ maxHeight: "70vh", overflowY: "auto" }}>
            {filteredCharacters.length === 0 ? (
              <p>No se encontraron personajes</p>
            ) : (
              filteredCharacters.map((character, index) => (
                <DraggableCharacter key={index} character={character} />
              ))
            )}
          </div>
        </div>

        {/* Sección derecha: Tarjetas */}
        <div style={{ flex: 2 }}>
          <h2>Tarjetas</h2>
          
          <div style={{ display: "flex", gap: "10px", marginBottom: "20px" }}>
            <input
              type="text"
              placeholder="Título para nueva tarjeta"
              value={newCardTitle}
              onChange={(e) => setNewCardTitle(e.target.value)}
              style={{ flex: 1, padding: "10px" }}
            />
            <button
              onClick={addNewCard}
              style={{
                padding: "10px 20px",
                background: "#2196F3",
                color: "white",
                border: "none",
                borderRadius: "4px",
                cursor: "pointer",
              }}
            >
              Crear Tarjeta
            </button>
          </div>
          
          <div style={{ maxHeight: "70vh", overflowY: "auto" }}>
            {cards.length === 0 ? (
              <p>No hay tarjetas creadas</p>
            ) : (
              cards.map((card) => (
                <CardSlot
                  key={card.id}
                  card={card}
                  onUpdateCard={updateCard}
                  onDeleteCard={deleteCard}
                />
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

// Componente wrapper para manejar DnD
export default function AdminPanelWrapper(props) {
  const backend = isMobile ? TouchBackend : HTML5Backend;
  
  return (
    <DndProvider backend={backend}>
      <AdminPanel {...props} />
    </DndProvider>
  );
}
