import React, { useEffect, useMemo, useRef, useState } from "react";

// Canales por defecto
const CHANNELS = [
  "https://www.twitch.tv/trickmasteh",
  "https://www.twitch.tv/blawbberto",
  "https://www.twitch.tv/kaosniight",
  "https://www.twitch.tv/khangsoofly",
  "https://www.twitch.tv/faaaxy",
  "https://www.twitch.tv/takeshijoga",
];

/**
 * Renderiza SOLO las tarjetas de canales que están LIVE.
 * Thumbnails compactos (110px) y estilos sobrios.
 */
export default function Streamings({
  channels = CHANNELS,
  pollIntervalMs = 60000,
  maxCards = 12,
}) {
  const [liveList, setLiveList] = useState([]); // [{channel, thumbUrl}]
  const [loading, setLoading] = useState(true);
  const timerRef = useRef(null);

  const normalizedChannels = useMemo(
    () =>
      (channels || [])
        .map((raw) => {
          if (!raw) return null;
          const s = String(raw).trim();
          try {
            if (s.startsWith("http")) {
              const u = new URL(s);
              const parts = u.pathname.split("/").filter(Boolean);
              return parts[0]?.toLowerCase() || null;
            }
          } catch {}
          return s.replace(/^@/, "").replace(/\s+/g, "").toLowerCase();
        })
        .filter(Boolean)
        .slice(0, maxCards),
    [channels, maxCards]
  );

  const checkLive = async () => {
    setLoading(true);

    const checks = normalizedChannels.map(
      (ch) =>
        new Promise((resolve) => {
          const img = new Image();
          const base = `https://static-cdn.jtvnw.net/previews-ttv/live_user_${encodeURIComponent(
            ch
          )}`;
          const candidates = [
            `${base}-320x180.jpg?cb=${Date.now()}`,
            `${base}-440x248.jpg?cb=${Date.now()}`,
          ];

          let done = false;
          const tryNext = (idx) => {
            if (idx >= candidates.length) {
              if (!done) resolve({ channel: ch, ok: false });
              return;
            }
            img.onload = () => {
              if (!done) {
                done = true;
                resolve({ channel: ch, ok: true, url: candidates[idx] });
              }
            };
            img.onerror = () => tryNext(idx + 1);
            img.src = candidates[idx];
          };
          tryNext(0);

          // timeout de seguridad
          setTimeout(() => { if (!done) resolve({ channel: ch, ok: false }); }, 5000);
        })
    );

    const results = await Promise.all(checks);
    const lives = results
      .filter((r) => r.ok)
      .map((r) => ({ channel: r.channel, thumbUrl: r.url }));

    setLiveList(lives);
    setLoading(false);
  };

  useEffect(() => {
    if (!normalizedChannels.length) {
      setLiveList([]);
      setLoading(false);
      return;
    }
    checkLive();
    timerRef.current = setInterval(checkLive, pollIntervalMs);
    return () => clearInterval(timerRef.current);
  }, [normalizedChannels, pollIntervalMs]);

  const IMG_HEIGHT = 100; // ← miniatura compacta

  return (
    <div style={{ display: "flex", flexDirection: "column", gap: 12 }}>
      {loading && liveList.length === 0 ? (
        <div style={{ ...styles.skeletonBox, height: IMG_HEIGHT }}>
          <div style={styles.livePillMuted}>Buscando LIVE…</div>
        </div>
      ) : (
        liveList.map((s) => (
          <a
            key={s.channel}
            href={`https://www.twitch.tv/${encodeURIComponent(s.channel)}`}
            target="_blank"
            rel="noopener noreferrer"
            style={{ textDecoration: "none" }}
          >
            <div style={styles.card}>
              <div style={styles.livePill}>LIVE</div>
              <img
                src={s.thumbUrl}
                alt={`${s.channel} live preview`}
                style={{
                  width: "100%",
                  height: IMG_HEIGHT,
                  objectFit: "cover",
                  display: "block",
                  background: "#555",
                }}
              />
              <div style={styles.meta}>
                <div style={styles.streamTitle}>{s.channel}</div>
                <div style={styles.streamSub}>Click para abrir en Twitch</div>
              </div>
            </div>
          </a>
        ))
      )}
    </div>
  );
}

const styles = {
  card: {
    position: "relative",
    border: "3px solid #000",
    borderRadius: 10,
    background: "#f7f9fc",
    overflow: "hidden",
  },
  skeletonBox: {
    border: "3px solid #000",
    borderRadius: 10,
    background: "#f7f9fc",
    display: "flex",
    alignItems: "flex-start",
  },
  livePill: {
    position: "absolute",
    top: 8,
    left: 8,
    background: "#ff3b30",
    color: "#fff",
    fontSize: 12,
    padding: "2px 8px",
    borderRadius: 999,
    fontWeight: 700,
    letterSpacing: 0.5,
    zIndex: 2,
  },
  livePillMuted: {
    margin: 8,
    background: "#e0e0e0",
    color: "#555",
    fontSize: 12,
    padding: "2px 8px",
    borderRadius: 999,
    fontWeight: 600,
    letterSpacing: 0.5,
  },
  meta: { padding: "8px 10px 10px" },
  streamTitle: { fontWeight: 700, color: "#1e2a38", marginBottom: 2, textTransform: "capitalize", fontSize: 14 },
  streamSub: { fontSize: 11, color: "#66788a" },
};
