import React, { useState, useMemo } from "react";
import { DndProvider, useDrag, useDrop } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import { TouchBackend } from "react-dnd-touch-backend";
import { isMobile } from "react-device-detect";

const DraggableCharacter = ({ character }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: "character",
    item: character,
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  return (
    <div
      ref={drag}
      style={{
        opacity: isDragging ? 0.5 : 1,
        padding: "10px",
        margin: "5px 0",
        border: "1px solid #ddd",
        borderRadius: "4px",
        backgroundColor: "#f9f9f9",
        cursor: "grab",
      }}
    >
      <strong>{character.name}</strong> - {character.label}
    </div>
  );
};

const CardSlot = ({ card, onAddCharacter, onUpdateCardData, onRemoveCharacter, onDeleteCard }) => {
  const [{ isOver }, drop] = useDrop(() => ({
    accept: "character",
    drop: (item) => onAddCharacter(card.id, item),
    collect: (monitor) => ({
      isOver: !!monitor.isOver(),
    }),
  }));

  const handleDateChange = (e) => {
    onUpdateCardData(card.id, { date: e.target.value });
  };

  const handleTimeChange = (e) => {
    onUpdateCardData(card.id, { time: e.target.value });
  };

  return (
    <div
      ref={drop}
      style={{
        border: `2px dashed ${isOver ? "#4CAF50" : "#ccc"}`,
        padding: "15px",
        marginBottom: "20px",
        borderRadius: "8px",
        backgroundColor: isOver ? "#f0fff0" : "#fff",
        minHeight: "150px",
        position: "relative",
      }}
    >
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h3 style={{ margin: 0 }}>{card.title}</h3>
        <button
          onClick={() => onDeleteCard(card.id)}
          style={{
            background: "#ff4d4d",
            color: "white",
            border: "none",
            borderRadius: "4px",
            padding: "5px 10px",
            cursor: "pointer",
          }}
        >
          Eliminar
        </button>
      </div>

      <div style={{ display: "flex", gap: "10px", margin: "10px 0" }}>
        <div>
          <label>Fecha: </label>
          <input
            type="date"
            value={card.date || ""}
            onChange={handleDateChange}
            style={{ padding: "5px" }}
          />
        </div>
        <div>
          <label>Hora: </label>
          <input
            type="time"
            value={card.time || ""}
            onChange={handleTimeChange}
            style={{ padding: "5px" }}
          />
        </div>
      </div>

      <div style={{ marginTop: "10px" }}>
        <strong>
          Personajes ({card.characters.length}/8):
        </strong>
        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            gap: "5px",
            marginTop: "5px",
          }}
        >
          {card.characters.map((character, index) => (
            <div
              key={index}
              style={{
                background: "#e0f7fa",
                padding: "5px 10px",
                borderRadius: "4px",
                display: "flex",
                alignItems: "center",
              }}
            >
              <span>{character.name}</span>
              <button
                onClick={() => onRemoveCharacter(card.id, index)}
                style={{
                  background: "none",
                  border: "none",
                  color: "#ff4d4d",
                  cursor: "pointer",
                  marginLeft: "5px",
                }}
              >
                ×
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

const AdminPanel = ({ cards, allCharacters, onSaveCards }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [newCardTitle, setNewCardTitle] = useState("");

  const filteredCharacters = useMemo(() => {
    if (!searchTerm) return allCharacters;
    return allCharacters.filter(
      (char) =>
        char.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        char.label.toLowerCase().includes(searchTerm.toLowerCase())
    );
  }, [allCharacters, searchTerm]);

  const addCharacterToCard = (cardId, character) => {
    const updatedCards = cards.map((card) => {
      if (card.id === cardId) {
        if (card.characters.length >= 8) {
          alert("No se pueden agregar más de 8 personajes a una tarjeta");
          return card;
        }

        if (card.characters.some(c => c.name === character.name && c.userEmail === character.userEmail)) {
          alert("Este personaje ya está en la tarjeta");
          return card;
        }

        return {
          ...card,
          characters: [...card.characters, character],
        };
      }
      return card;
    });
    onSaveCards(updatedCards);
  };

  const updateCardData = (cardId, data) => {
    const updatedCards = cards.map((card) => {
      if (card.id === cardId) {
        return {
          ...card,
          ...data,
          characters: card.characters, // preserva personajes
        };
      }
      return card;
    });
    onSaveCards(updatedCards);
  };

  const removeCharacterFromCard = (cardId, characterIndex) => {
    const updatedCards = cards.map((card) => {
      if (card.id === cardId) {
        return {
          ...card,
          characters: card.characters.filter((_, i) => i !== characterIndex),
        };
      }
      return card;
    });
    onSaveCards(updatedCards);
  };

  const deleteCard = (cardId) => {
    const updatedCards = cards.filter(card => card.id !== cardId);
    onSaveCards(updatedCards);
  };

  const addNewCard = () => {
    if (!newCardTitle.trim()) return;

    const newCard = {
      id: Date.now(),
      title: newCardTitle.trim(),
      characters: [], // array vacío al crear
      date: "",
      time: "",
    };

    onSaveCards([...cards, newCard]);
    setNewCardTitle("");
  };

  const publishCards = () => {
    const incompleteCards = cards.filter(
      (card) => card.characters.length === 0 || !card.date || !card.time
    );
    if (incompleteCards.length > 0) {
      alert("Algunas tarjetas están incompletas. Deben tener al menos un personaje, fecha y hora.");
      return;
    }
    alert("¡Tarjetas publicadas con éxito!");
  };

  return (
    <div style={{ padding: 20, maxWidth: 1200, margin: "0 auto" }}>
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 20 }}>
        <h1>Panel de Administrador</h1>
        <button
          onClick={publishCards}
          style={{ padding: "10px 20px", background: "#4caf50", color: "white", border: "none", borderRadius: 4, cursor: "pointer", fontSize: 16 }}
        >
          Publicar Tarjetas
        </button>
      </div>

      <div style={{ display: "flex", gap: 20, marginTop: 20 }}>
        <div style={{ flex: 1 }}>
          <h2>Personajes Disponibles</h2>
          <input
            type="text"
            placeholder="Buscar personajes..."
            value={searchTerm}
            onChange={e => setSearchTerm(e.target.value)}
            style={{ width: "100%", padding: 10, marginBottom: 15, border: "1px solid #ddd", borderRadius: 4 }}
          />
          <div style={{ maxHeight: "70vh", overflowY: "auto" }}>
            {filteredCharacters.length === 0 ? (
              <p>No se encontraron personajes</p>
            ) : (
              filteredCharacters.map((character, index) => (
                <DraggableCharacter key={index} character={character} />
              ))
            )}
          </div>
        </div>

        <div style={{ flex: 2 }}>
          <h2>Tarjetas</h2>
          <div style={{ display: "flex", gap: 10, marginBottom: 20 }}>
            <input
              type="text"
              placeholder="Título para nueva tarjeta"
              value={newCardTitle}
              onChange={e => setNewCardTitle(e.target.value)}
              style={{ flex: 1, padding: 10 }}
            />
            <button
              onClick={addNewCard}
              style={{ padding: "10px 20px", background: "#2196f3", color: "white", border: "none", borderRadius: 4, cursor: "pointer" }}
            >
              Crear Tarjeta
            </button>
          </div>
          <div style={{ maxHeight: "70vh", overflowY: "auto" }}>
            {cards.length === 0 ? (
              <p>No hay tarjetas creadas</p>
            ) : (
              cards.map(card => (
                <CardSlot
                  key={card.id}
                  card={card}
                  onAddCharacter={addCharacterToCard}
                  onUpdateCardData={updateCardData}
                  onRemoveCharacter={removeCharacterFromCard}
                  onDeleteCard={deleteCard}
                />
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default function AdminPanelWrapper(props) {
  const backend = isMobile ? TouchBackend : HTML5Backend;
  return (
    <DndProvider backend={backend}>
      <AdminPanel {...props} />
    </DndProvider>
  );
}
