import React, { useState, useMemo } from "react";
import CardSlot from "./CardSlot";

const AdminPanel = ({ cards, allCharacters, onSaveCards }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [newCardTitle, setNewCardTitle] = useState("");

  const filteredCharacters = useMemo(() => {
    if (!searchTerm) return allCharacters;
    return allCharacters.filter(
      (char) =>
        char.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (char.label && char.label.toLowerCase().includes(searchTerm.toLowerCase()))
    );
  }, [allCharacters, searchTerm]);

  const addCharacterToCard = (cardId, character) => {
    const updatedCards = cards.map((card) => {
      if (card.id === cardId) {
        if (card.characters.length >= 8) {
          alert("No se pueden agregar más de 8 personajes a una tarjeta");
          return card;
        }
        if (
          card.characters.some(
            (c) => c.name === character.name && c.userEmail === character.userEmail
          )
        ) {
          alert("Este personaje ya está en la tarjeta");
          return card;
        }
        return {
          ...card,
          characters: [...card.characters, character]
        };
      }
      return card;
    });
    onSaveCards(updatedCards);
  };

  const updateCardData = (cardId, data) => {
    const updatedCards = cards.map((card) => {
      if (card.id === cardId) {
        return {
          ...card,
          ...data,
          characters: card.characters
        };
      }
      return card;
    });
    onSaveCards(updatedCards);
  };

  const removeCharacterFromCard = (cardId, characterIndex) => {
    const updatedCards = cards.map((card) => {
      if (card.id === cardId) {
        return {
          ...card,
          characters: card.characters.filter((_, i) => i !== characterIndex)
        };
      }
      return card;
    });
    onSaveCards(updatedCards);
  };

  const deleteCard = (cardId) => {
    const updatedCards = cards.filter((card) => card.id !== cardId);
    onSaveCards(updatedCards);
  };

  const addNewCard = () => {
    if (!newCardTitle.trim()) return;

    const newCard = {
      id: Date.now(),
      title: newCardTitle.trim(),
      characters: [],
      date: "",
      time: ""
    };

    onSaveCards([...cards, newCard]);
    setNewCardTitle("");
  };

  return (
    <div style={{ padding: 20, maxWidth: 1200, margin: "0 auto" }}>
      <h1>Panel de Administración</h1>
      <div style={{ marginBottom: 20 }}>
        <input
          type="text"
          placeholder="Buscar personajes..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ width: "100%", padding: 10, marginBottom: 15, borderRadius: 4, border: "1px solid #ddd" }}
        />
        <div style={{ maxHeight: 300, overflowY: "auto" }}>
          {filteredCharacters.length === 0 ? (
            <p>No se encontraron personajes</p>
          ) : (
            filteredCharacters.map((char, idx) => (
              <div
                key={idx}
                style={{
                  padding: 8,
                  border: "1px solid #ccc",
                  marginBottom: 5,
                  borderRadius: 4,
                  cursor: "grab"
                }}
                draggable
                onDragStart={(e) => {
                  e.dataTransfer.setData("character", JSON.stringify(char));
                }}
              >
                {char.name} {char.label ? `- ${char.label}` : ""}
              </div>
            ))
          )}
        </div>
      </div>

      <div style={{ marginBottom: 20, display: "flex", gap: 10 }}>
        <input
          type="text"
          placeholder="Título para nueva tarjeta"
          value={newCardTitle}
          onChange={(e) => setNewCardTitle(e.target.value)}
          style={{ flex: 1, padding: 10 }}
        />
        <button onClick={addNewCard} style={{ padding: "10px 20px", cursor: "pointer" }}>
          Crear Tarjeta
        </button>
      </div>

      <div>
        {cards.length === 0 ? (
          <p>No hay tarjetas creadas</p>
        ) : (
          cards.map((card) => (
            <CardSlot
              key={card.id}
              card={card}
              onAddCharacter={addCharacterToCard}
              onUpdateCardData={updateCardData}
              onRemoveCharacter={removeCharacterFromCard}
              onDeleteCard={deleteCard}
            />
          ))
        )}
      </div>
    </div>
  );
};

export default AdminPanel;
