import React, { useState, useMemo } from "react";
import Modal from "react-modal";
import Select from "react-select";

Modal.setAppElement("#root"); // Ajusta si tu app root es otro

const AdminPanel = ({ cards, allCharacters, onSaveCards }) => {
  const [newCardTitle, setNewCardTitle] = useState("");
  const [modalOpen, setModalOpen] = useState(false);
  const [editingCardId, setEditingCardId] = useState(null);
  const [selectedCharacters, setSelectedCharacters] = useState([]);
  const [selectedDate, setSelectedDate] = useState("");
  const [selectedTime, setSelectedTime] = useState("");

  const options = useMemo(
    () =>
      allCharacters.map((char) => ({
        value: char.name,
        label: char.name,
      })),
    [allCharacters]
  );

  // Abrir modal para nueva tarjeta o editar
  const openModal = (card) => {
    setEditingCardId(card?.id || null);
    setSelectedCharacters(
      card
        ? card.characters.map((c) => ({ value: c.name, label: c.name }))
        : []
    );
    setSelectedDate(card?.date || "");
    setSelectedTime(card?.time || "");
    setModalOpen(true);
  };

  // Guardar cambios del modal
  const saveModal = () => {
    if (!newCardTitle.trim() && !editingCardId) {
      alert("Debe ingresar un título para la tarjeta");
      return;
    }
    if (selectedCharacters.length === 0) {
      alert("Debe seleccionar al menos un personaje");
      return;
    }
    if (selectedCharacters.length > 8) {
      alert("No puedes seleccionar más de 8 personajes");
      return;
    }

    const characters = selectedCharacters.map((c) => ({ name: c.value }));

    if (editingCardId) {
      // Editar tarjeta existente
      const updatedCards = cards.map((card) =>
        card.id === editingCardId
          ? {
              ...card,
              characters,
              date: selectedDate,
              time: selectedTime,
            }
          : card
      );
      onSaveCards(updatedCards);
    } else {
      // Crear nueva tarjeta
      const newCard = {
        id: Date.now(),
        title: newCardTitle.trim(),
        characters,
        date: selectedDate,
        time: selectedTime,
      };
      onSaveCards([...cards, newCard]);
      setNewCardTitle("");
    }

    setModalOpen(false);
  };

  const deleteCard = (cardId) => {
    if (window.confirm("¿Seguro que quieres eliminar esta tarjeta?")) {
      onSaveCards(cards.filter((card) => card.id !== cardId));
    }
  };

  return (
    <div style={{ padding: 20, maxWidth: 900, margin: "0 auto" }}>
      <h1>Panel de Administración</h1>
      <div style={{ marginBottom: 20, display: "flex", gap: 10 }}>
        <input
          type="text"
          placeholder="Título para nueva tarjeta"
          value={newCardTitle}
          onChange={(e) => setNewCardTitle(e.target.value)}
          style={{ flex: 1, padding: 10 }}
        />
        <button onClick={() => openModal(null)} style={{ padding: "10px 20px" }}>
          Crear Tarjeta
        </button>
      </div>

      <div>
        {cards.length === 0 ? (
          <p>No hay tarjetas creadas</p>
        ) : (
          cards.map((card) => (
            <div
              key={card.id}
              style={{
                border: "1px solid #ccc",
                borderRadius: 6,
                padding: 12,
                marginBottom: 16,
                background: "#fafafa",
              }}
            >
              <h3>{card.title}</h3>
              <p>Fecha: {card.date || "No asignada"}</p>
              <p>Hora: {card.time || "No asignada"}</p>
              <div>
                <strong>Personajes:</strong>
                <ul>
                  {card.characters.map((c, i) => (
                    <li key={i}>{c.name}</li>
                  ))}
                </ul>
              </div>
              <button onClick={() => openModal(card)} style={{ cursor: "pointer" }}>
                Editar Tarjeta
              </button>{" "}
              <button
                onClick={() => deleteCard(card.id)}
                style={{ cursor: "pointer", color: "red" }}
              >
                Eliminar Tarjeta
              </button>
            </div>
          ))
        )}
      </div>

      <Modal
        isOpen={modalOpen}
        onRequestClose={() => setModalOpen(false)}
        contentLabel="Seleccionar Personajes"
        style={{
          content: {
            maxWidth: "600px",
            margin: "auto",
            height: "fit-content",
            padding: "20px",
          },
        }}
      >
        <h2>{editingCardId ? "Editar Tarjeta" : "Nueva Tarjeta"}</h2>
        {!editingCardId && (
          <input
            type="text"
            placeholder="Título para la tarjeta"
            value={newCardTitle}
            onChange={(e) => setNewCardTitle(e.target.value)}
            style={{ width: "100%", padding: 10, marginBottom: 15 }}
          />
        )}

        <label>
          Fecha:
          <input
            type="date"
            value={selectedDate}
            onChange={(e) => setSelectedDate(e.target.value)}
            style={{ width: "100%", padding: 8, marginBottom: 15 }}
          />
        </label>

        <label>
          Hora:
          <input
            type="time"
            value={selectedTime}
            onChange={(e) => setSelectedTime(e.target.value)}
            style={{ width: "100%", padding: 8, marginBottom: 15 }}
          />
        </label>

        <label>Selecciona personajes (máx 8):</label>
        <Select
          isMulti
          options={options}
          value={selectedCharacters}
          onChange={setSelectedCharacters}
          placeholder="Buscar personajes..."
          closeMenuOnSelect={false}
        />

        <div style={{ marginTop: 20, display: "flex", justifyContent: "flex-end", gap: 10 }}>
          <button onClick={() => setModalOpen(false)}>Cancelar</button>
          <button onClick={saveModal} style={{ backgroundColor: "#4caf50", color: "white" }}>
            Guardar
          </button>
        </div>
      </Modal>
    </div>
  );
};

export default AdminPanel;
