import React, { useState, useMemo } from "react";
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";

const MySwal = withReactContent(Swal);

const AdminPanel = ({ cards, allCharacters, onSaveCards }) => {
  const [newCardTitle, setNewCardTitle] = useState("");

  const options = useMemo(
    () =>
      allCharacters.map((char) => ({
        value: char.name,
        label: char.name,
      })),
    [allCharacters]
  );

  const addNewCard = async () => {
    if (!newCardTitle.trim()) return;

    const { value: selectedCharacters } = await MySwal.fire({
      title: "Selecciona personajes (máx 8)",
      width: 600,
      heightAuto: false,
      html: `<select id="characters-select" multiple style="width: 100%; height: 200px; font-size: 16px;">
        ${allCharacters
          .map((char) => `<option value="${char.name}">${char.name}</option>`)
          .join("")}
      </select>`,
      focusConfirm: false,
      preConfirm: () => {
        const select = document.getElementById("characters-select");
        if (select) {
          return Array.from(select.selectedOptions).map((opt) => opt.value);
        }
        return [];
      },
      showCancelButton: true,
    });

    if (!selectedCharacters) return;

    if (selectedCharacters.length > 8) {
      MySwal.fire("Error", "Solo puedes seleccionar máximo 8 personajes", "error");
      return;
    }

    const characters = allCharacters.filter((char) =>
      selectedCharacters.includes(char.name)
    );

    const newCard = {
      id: Date.now(),
      title: newCardTitle.trim(),
      characters,
      date: "",
      time: "",
    };

    onSaveCards([...cards, newCard]);
    setNewCardTitle("");
  };

  return (
    <div style={{ padding: 20, maxWidth: 800, margin: "0 auto" }}>
      <h1>Panel de Administración</h1>

      <div style={{ marginBottom: 20, display: "flex", gap: 10 }}>
        <input
          type="text"
          placeholder="Título para nueva tarjeta"
          value={newCardTitle}
          onChange={(e) => setNewCardTitle(e.target.value)}
          style={{ flex: 1, padding: 10 }}
        />
        <button onClick={addNewCard} style={{ padding: "10px 20px", cursor: "pointer" }}>
          Crear Tarjeta
        </button>
      </div>

      <div>
        {cards.length === 0 ? (
          <p>No hay tarjetas creadas</p>
        ) : (
          cards.map((card) => (
            <div
              key={card.id}
              style={{
                border: "1px solid #ccc",
                borderRadius: 6,
                padding: 12,
                marginBottom: 16,
                background: "#fafafa",
              }}
            >
              <h3>{card.title}</h3>
              <p>Fecha: {card.date || "No asignada"}</p>
              <p>Hora: {card.time || "No asignada"}</p>
              <div>
                <strong>Personajes:</strong>
                <ul>
                  {card.characters.map((c, i) => (
                    <li key={i}>{c.name}</li>
                  ))}
                </ul>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default AdminPanel;
