// frontend/src/components/admin/MiscellaneumClasses.jsx
import React, { useMemo } from "react";
import { CLASSES } from "../../constants/classes";

export default function MiscellaneumClasses({
  title = "Clases",
  sortMode = "asIs",
  customOrder = [],
}) {
  const classesOrdered = useMemo(() => {
    if (!Array.isArray(CLASSES)) return [];

    if (sortMode === "alpha") {
      return [...CLASSES].sort((a, b) => a.name.localeCompare(b.name, "es"));
    }

    if (sortMode === "custom" && Array.isArray(customOrder) && customOrder.length) {
      const indexMap = new Map(customOrder.map((k, i) => [k, i]));
      return [...CLASSES].sort((a, b) => {
        const ia = indexMap.has(a.key) ? indexMap.get(a.key) : Number.MAX_SAFE_INTEGER;
        const ib = indexMap.has(b.key) ? indexMap.get(b.key) : Number.MAX_SAFE_INTEGER;
        if (ia === ib) return a.name.localeCompare(b.name, "es");
        return ia - ib;
      });
    }

    // asIs -> respeta el orden original según "dónde están ubicados"
    return CLASSES;
  }, [sortMode, customOrder]);

  return (
    <aside
      style={{
        position: "fixed",
        bottom: 16,
        right: 16,
        width: 520, // ancho pensado para 6 columnas cómodas
        maxWidth: "90vw",
        background: "#ffffff",
        borderRadius: 12,
        boxShadow: "0 10px 30px rgba(0,0,0,0.12)",
        border: "1px solid #e5e7eb",
        padding: 12,
        zIndex: 50,
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      }}
    >
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          marginBottom: 8,
        }}
      >
        <h4
          style={{
            margin: 0,
            color: "#111827",
            fontSize: 16,
            fontWeight: 700,
            letterSpacing: 0.2,
          }}
        >
          {title}
        </h4>

        {/* botón para minimizar/ocultar si lo quieres implementar luego */}
        {/* <button style={{...}}>—</button> */}
      </div>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(6, minmax(0, 1fr))",
          gap: 8,
          alignItems: "stretch",
        }}
      >
        {classesOrdered.map((cls) => (
          <div
            key={cls.key}
            title={cls.name}
            style={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              gap: 6,
              padding: 8,
              borderRadius: 10,
              background: "#f9fafb",
              border: "1px solid #eef2f7",
            }}
          >
            {cls.image ? (
              <img
                src={cls.image}
                alt={cls.name}
                style={{
                  width: 36,
                  height: 36,
                  borderRadius: 8,
                  objectFit: "contain",
                }}
                loading="lazy"
                decoding="async"
              />
            ) : (
              <div
                aria-hidden
                style={{
                  width: 36,
                  height: 36,
                  borderRadius: 8,
                  background: "#e5e7eb",
                  display: "grid",
                  placeItems: "center",
                  fontSize: 12,
                  color: "#6b7280",
                }}
              >
                ?
              </div>
            )}

            <span
              style={{
                fontSize: 11,
                lineHeight: 1.2,
                color: "#374151",
                textAlign: "center",
                whiteSpace: "nowrap",
                overflow: "hidden",
                textOverflow: "ellipsis",
                width: "100%",
              }}
            >
              {cls.name}
            </span>
          </div>
        ))}
      </div>
    </aside>
  );
}
