import React from "react";
import { Link } from "react-router-dom";

export default function HomePage({ allUsersData }) {
  // Extraer todos los personajes de todos los usuarios
  const allCharacters = [];
  Object.values(allUsersData).forEach(userData => {
    if (userData.characters) {
      userData.characters.forEach(character => {
        allCharacters.push({
          ...character,
          userEmail: userData.email
        });
      });
    }
  });

  return (
    <div style={{ 
      maxWidth: "1200px", 
      margin: "20px auto", 
      padding: "20px"
    }}>
      <div style={{ 
        textAlign: "center", 
        marginBottom: "40px",
        padding: "20px",
        backgroundColor: "#f8f9fa",
        borderRadius: "8px"
      }}>
        <h1>Bienvenido a WhaleCorp</h1>
        <p style={{ fontSize: "18px", maxWidth: "600px", margin: "0 auto" }}>
          Explora nuestra colección de personajes marinos históricos y sus fascinantes historias
        </p>
      </div>

      <h2 style={{ textAlign: "center" }}>Personajes Destacados</h2>
      
      {allCharacters.length === 0 ? (
        <div style={{ textAlign: "center", margin: "50px 0" }}>
          <p style={{ fontSize: "18px", color: "#6c757d" }}>Aún no hay personajes registrados</p>
          <Link 
            to="/login" 
            style={{
              display: "inline-block",
              marginTop: "20px",
              padding: "10px 20px",
              background: "#007bff",
              color: "white",
              textDecoration: "none",
              borderRadius: "4px"
            }}
          >
            Inicia sesión para agregar personajes
          </Link>
        </div>
      ) : (
        <div style={{ 
          display: "grid", 
          gridTemplateColumns: "repeat(auto-fill, minmax(300px, 1fr))", 
          gap: "30px",
          marginTop: "30px"
        }}>
          {allCharacters.map((character, index) => (
            <div 
              key={index} 
              style={{
                border: "1px solid #dee2e6",
                borderRadius: "8px",
                overflow: "hidden",
                boxShadow: "0 4px 6px rgba(0,0,0,0.05)",
                transition: "transform 0.3s ease",
                ":hover": {
                  transform: "translateY(-5px)"
                }
              }}
            >
              <div style={{ padding: "20px" }}>
                <h3 style={{ marginTop: 0, color: "#007bff" }}>{character.name}</h3>
                <div style={{ 
                  display: "inline-block",
                  background: "#e9ecef",
                  padding: "3px 10px",
                  borderRadius: "20px",
                  marginBottom: "15px",
                  fontSize: "14px"
                }}>
                  {character.label}
                </div>
                
                <div style={{ display: "flex", gap: "15px", margin: "15px 0" }}>
                  <div>
                    <div style={{ fontSize: "12px", color: "#6c757d" }}>FECHA</div>
                    <div>{character.date}</div>
                  </div>
                  <div>
                    <div style={{ fontSize: "12px", color: "#6c757d" }}>HORA</div>
                    <div>{character.time}</div>
                  </div>
                </div>
                
                <div style={{ 
                  fontSize: "14px", 
                  color: "#6c757d", 
                  marginTop: "15px",
                  borderTop: "1px solid #eee",
                  paddingTop: "15px"
                }}>
                  Agregado por: {character.userEmail}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
