import React, { useEffect, useMemo, useRef, useState } from "react";

// Canales por defecto (puedes pasarlos por props)
const CHANNELS = [
  "https://www.twitch.tv/trickmasteh",
  "https://www.twitch.tv/blawbberto",
  "https://www.twitch.tv/kaosniight",
  "https://www.twitch.tv/khangsoofly",
  "https://www.twitch.tv/faaaxy",
  "https://www.twitch.tv/takeshijoga",
];

/**
 * Renderiza SOLO las tarjetas de canales que están LIVE.
 * Miniaturas angostas (cardWidth), relación 16:9, lazy + tamaños explícitos.
 * Pausa el polling cuando la pestaña está oculta.
 */
export default function Streamings({
  channels = CHANNELS,
  pollIntervalMs = 60000,
  maxCards = 12,
  cardWidth = 280, // << ancho de la tarjeta (más angosta)
}) {
  const [liveList, setLiveList] = useState([]); // [{channel, thumbUrl, width, height}]
  const [loading, setLoading] = useState(true);
  const timerRef = useRef(null);

  // Preconnect (mejora TTFB/cold start del CDN)
  useEffect(() => {
    const add = (href) => {
      const link = document.createElement("link");
      link.rel = "preconnect";
      link.href = href;
      link.crossOrigin = "";
      document.head.appendChild(link);
      return link;
    };
    const a = add("https://static-cdn.jtvnw.net");
    const b = add("https://www.twitch.tv");
    return () => { a.remove(); b.remove(); };
  }, []);

  const normalizedChannels = useMemo(
    () =>
      (channels || [])
        .map((raw) => {
          if (!raw) return null;
          const s = String(raw).trim();
          try {
            if (s.startsWith("http")) {
              const u = new URL(s);
              const parts = u.pathname.split("/").filter(Boolean);
              return parts[0]?.toLowerCase() || null;
            }
          } catch {}
          return s.replace(/^@/, "").replace(/\s+/g, "").toLowerCase();
        })
        .filter(Boolean)
        .slice(0, maxCards),
    [channels, maxCards]
  );

  const checkLive = async () => {
    setLoading(true);

    const checks = normalizedChannels.map(
      (ch) =>
        new Promise((resolve) => {
          const base = `https://static-cdn.jtvnw.net/previews-ttv/live_user_${encodeURIComponent(
            ch
          )}`;
          // Preferimos 320 (más chico) y dejamos 440 como fallback
          const candidates = [
            { url: `${base}-320x180.jpg?cb=${Date.now()}`, w: 320, h: 180 },
            { url: `${base}-440x248.jpg?cb=${Date.now()}`, w: 440, h: 248 },
          ];
          let idx = 0;
          const img = new Image();
          const tryLoad = () => {
            if (idx >= candidates.length) return resolve({ channel: ch, ok: false });
            img.onload = () => resolve({
              channel: ch,
              ok: true,
              url: candidates[idx].url,
              w: candidates[idx].w,
              h: candidates[idx].h
            });
            img.onerror = () => { idx += 1; tryLoad(); };
            img.decoding = "async";
            img.loading = "lazy";
            img.src = candidates[idx].url;
          };
          tryLoad();
          // timeout seguridad
          setTimeout(() => resolve({ channel: ch, ok: false }), 5000);
        })
    );

    const results = await Promise.all(checks);
    const lives = results.filter(r => r.ok).map(r => ({
      channel: r.channel,
      thumbUrl: r.url,
      w: r.w,
      h: r.h
    }));

    setLiveList(lives);
    setLoading(false);
  };

  // Pausar polling si la pestaña está oculta
  useEffect(() => {
    const tick = () => {
      if (document.visibilityState === "visible") checkLive();
    };
    tick();
    timerRef.current = setInterval(tick, pollIntervalMs);
    const vis = () => { if (document.visibilityState === "visible") checkLive(); };
    document.addEventListener("visibilitychange", vis);
    return () => {
      clearInterval(timerRef.current);
      document.removeEventListener("visibilitychange", vis);
    };
  }, [normalizedChannels, pollIntervalMs]); // eslint-disable-line react-hooks/exhaustive-deps

  const cardHeight = Math.round(cardWidth * 9 / 16);

  return (
    <div
      aria-label="Listado de streamings en vivo"
      style={{ display: "flex", flexDirection: "column", gap: 12 }}
    >
      {loading && liveList.length === 0 ? (
        <div style={{ ...styles.skeletonBox, width: cardWidth, height: cardHeight }}>
          <div style={styles.livePillMuted}>Looking for someone who’s live…</div>
        </div>
      ) : (
        liveList.map((s) => (
          <a
            key={s.channel}
            href={`https://www.twitch.tv/${encodeURIComponent(s.channel)}`}
            target="_blank"
            rel="noopener noreferrer"
            style={{ textDecoration: "none" }}
          >
            <div
              style={{
                ...styles.card,
                width: cardWidth,
                contentVisibility: "auto",
                containIntrinsicSize: `${cardHeight + 48}px`,
                marginLeft: "auto", // pega a la derecha
              }}
            >
              <div style={styles.livePill}>LIVE</div>

              <img
                src={s.thumbUrl}
                srcSet={`${s.thumbUrl} ${s.w}w`}
                sizes={`${cardWidth}px`}
                alt={`${s.channel} en vivo`}
                width={cardWidth}
                height={cardHeight}
                loading="lazy"
                decoding="async"
                fetchpriority="low"
                style={{
                  width: "100%",
                  height: cardHeight,
                  objectFit: "cover",
                  display: "block",
                  background: "#555",
                }}
              />

              <div style={styles.meta}>
                <div style={styles.streamTitle}>{s.channel}</div>
                <div style={styles.streamSub}>Click para abrir en Twitch</div>
              </div>
            </div>
          </a>
        ))
      )}
    </div>
  );
}

const styles = {
  card: {
    position: "relative",
    border: "3px solid #000",
    borderRadius: 10,
    background: "#f7f9fc",
    overflow: "hidden",
  },
  skeletonBox: {
    border: "3px solid #000",
    borderRadius: 10,
    background: "#f7f9fc",
    display: "flex",
    alignItems: "flex-start",
    marginLeft: "auto",
  },
  livePill: {
    position: "absolute",
    top: 8,
    left: 8,
    background: "#ff3b30",
    color: "#fff",
    fontSize: 12,
    padding: "2px 8px",
    borderRadius: 999,
    fontWeight: 700,
    letterSpacing: 0.5,
    zIndex: 2,
  },
  livePillMuted: {
    margin: 8,
    background: "#e0e0e0",
    color: "#555",
    fontSize: 12,
    padding: "2px 8px",
    borderRadius: 999,
    fontWeight: 600,
    letterSpacing: 0.5,
  },
  meta: { padding: "8px 10px 10px" },
  streamTitle: { fontWeight: 700, color: "#1e2a38", marginBottom: 2, textTransform: "capitalize", fontSize: 14 },
  streamSub: { fontSize: 11, color: "#66788a" },
};
