import React from "react";
import { useDrop } from "react-dnd";

const CardSlot = ({ card, onAddCharacter, onRemoveCharacter, onDeleteCard }) => {
  const [{ isOver }, drop] = useDrop(() => ({
    accept: "character",
    drop: (item) => onAddCharacter(card.id, item),
    collect: (monitor) => ({
      isOver: !!monitor.isOver(),
    }),
  }));

  return (
    <div
      ref={drop}
      style={{
        border: `2px dashed ${isOver ? "#4CAF50" : "#ccc"}`,
        padding: 15,
        borderRadius: 8,
        backgroundColor: isOver ? "#f0fff0" : "#fff",
        minHeight: 150,
        marginBottom: 20,
      }}
    >
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h3>{card.title}</h3>
        <button onClick={() => onDeleteCard(card.id)} style={{ cursor: "pointer" }}>
          Eliminar
        </button>
      </div>

      <div style={{ marginTop: 10 }}>
        <strong>Personajes ({card.characters.length}/8):</strong>
        <div style={{ display: "flex", flexWrap: "wrap", gap: 5, marginTop: 5 }}>
          {card.characters.map((char, idx) => (
            <div
              key={idx}
              style={{
                background: "#e0f7fa",
                padding: "5px 10px",
                borderRadius: 4,
                display: "flex",
                alignItems: "center",
              }}
            >
              <span>{char.name}</span>
              <button
                onClick={() => onRemoveCharacter(card.id, idx)}
                style={{ marginLeft: 5, cursor: "pointer", background: "none", border: "none", color: "#f44336" }}
              >
                Ã—
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CardSlot;
