import React, { useState, useMemo } from "react";
import { DndProvider, useDrag, useDrop } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";

const DraggableCharacter = ({ name }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: "CHARACTER",
    item: { name },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  return (
    <div
      ref={drag}
      style={{
        opacity: isDragging ? 0.5 : 1,
        padding: 8,
        border: "1px solid #ccc",
        margin: 4,
        borderRadius: 4,
        cursor: "grab",
      }}
    >
      {name}
    </div>
  );
};

const Card = ({ card, onDropCharacter, onRemoveCharacter, onDeleteCard }) => {
  const [{ isOver }, drop] = useDrop(() => ({
    accept: "CHARACTER",
    drop: (item) => onDropCharacter(card.id, item.name),
    collect: (monitor) => ({
      isOver: !!monitor.isOver(),
    }),
  }));

  return (
    <div
      ref={drop}
      style={{
        border: "2px dashed #666",
        backgroundColor: isOver ? "#e0ffe0" : "#fafafa",
        padding: 16,
        marginBottom: 20,
        minHeight: 100,
      }}
    >
      <h3>{card.title}</h3>
      <div style={{ display: "flex", flexWrap: "wrap", gap: 8, marginTop: 10 }}>
        {card.characters.map((name, idx) => (
          <div
            key={idx}
            style={{
              backgroundColor: "#cce",
              padding: "4px 8px",
              borderRadius: 4,
              display: "flex",
              alignItems: "center",
            }}
          >
            <span>{name}</span>
            <button
              onClick={() => onRemoveCharacter(card.id, idx)}
              style={{
                marginLeft: 8,
                cursor: "pointer",
                border: "none",
                background: "transparent",
                color: "red",
                fontWeight: "bold",
              }}
            >
              ×
            </button>
          </div>
        ))}
      </div>
      <button onClick={() => onDeleteCard(card.id)} style={{ marginTop: 10, cursor: "pointer" }}>
        Eliminar Tarjeta
      </button>
    </div>
  );
};

export default function AdminPanel({ cards, allCharacters, onSaveCards }) {
  const [searchTerm, setSearchTerm] = useState("");
  const [newCardTitle, setNewCardTitle] = useState("");

  const filteredCharacters = useMemo(() => {
    if (!searchTerm) return allCharacters;
    return allCharacters.filter((char) =>
      char.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
  }, [allCharacters, searchTerm]);

  const onDropCharacter = (cardId, name) => {
    const updatedCards = cards.map((card) => {
      if (card.id === cardId) {
        if (card.characters.length >= 8) {
          alert("No se pueden agregar más de 8 personajes a una tarjeta");
          return card;
        }
        if (card.characters.includes(name)) {
          alert("Este personaje ya está en la tarjeta");
          return card;
        }
        return { ...card, characters: [...card.characters, name] };
      }
      return card;
    });
    onSaveCards(updatedCards);
  };

  const onRemoveCharacter = (cardId, index) => {
    const updatedCards = cards.map((card) => {
      if (card.id === cardId) {
        const newChars = [...card.characters];
        newChars.splice(index, 1);
        return { ...card, characters: newChars };
      }
      return card;
    });
    onSaveCards(updatedCards);
  };

  const onDeleteCard = (cardId) => {
    const updatedCards = cards.filter((card) => card.id !== cardId);
    onSaveCards(updatedCards);
  };

  const onAddCard = () => {
    if (!newCardTitle.trim()) return;
    const newCard = { id: Date.now(), title: newCardTitle.trim(), characters: [] };
    onSaveCards([...cards, newCard]);
    setNewCardTitle("");
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div style={{ maxWidth: 600, margin: "auto", padding: 20 }}>
        <h2>Personajes Disponibles</h2>
        <input
          type="text"
          placeholder="Buscar personajes"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ width: "100%", padding: 8, marginBottom: 15 }}
        />
        <div style={{ display: "flex", flexWrap: "wrap", gap: 8, maxHeight: 300, overflowY: "auto" }}>
          {filteredCharacters.length === 0 ? (
            <p>No se encontraron personajes</p>
          ) : (
            filteredCharacters.map((char) => <DraggableCharacter key={char.name} name={char.name} />)
          )}
        </div>

        <div style={{ marginTop: 20, display: "flex", gap: 10 }}>
          <input
            type="text"
            placeholder="Título nueva tarjeta"
            value={newCardTitle}
            onChange={(e) => setNewCardTitle(e.target.value)}
            style={{ flex: 1, padding: 8 }}
          />
          <button onClick={onAddCard} style={{ padding: "8px 16px" }}>
            Crear Tarjeta
          </button>
        </div>

        <div style={{ marginTop: 20 }}>
          {cards.length === 0 ? (
            <p>No hay tarjetas creadas</p>
          ) : (
            cards.map((card) => (
              <Card
                key={card.id}
                card={card}
                onDropCharacter={onDropCharacter}
                onRemoveCharacter={onRemoveCharacter}
                onDeleteCard={onDeleteCard}
              />
            ))
          )}
        </div>
      </div>
    </DndProvider>
  );
}
