import React, { useEffect, useRef, useState } from "react";

/**
 * Streamings (frontend-only, SIN keys)
 * - Acepta canales como username ("kaosniight") o URL completa ("https://www.twitch.tv/kaosniight")
 * - Detecta LIVE cargando la miniatura pública de Twitch.
 * - Si está LIVE, muestra tarjeta clickeable que abre el canal.
 */
export default function Streamings({
  channels = [],
  pollIntervalMs = 60000,
  maxCards = 12,
}) {
  const [liveList, setLiveList] = useState([]); // [{channel, thumbUrl}]
  const [loading, setLoading] = useState(true);
  const timerRef = useRef(null);

  const normalizeChannels = (arr) =>
    (arr || [])
      .map((raw) => {
        if (!raw) return null;
        const s = String(raw).trim();
        // Si viene URL, extrae el último segmento (user)
        try {
          if (s.startsWith("http")) {
            const u = new URL(s);
            const parts = u.pathname.split("/").filter(Boolean);
            return parts[0]?.toLowerCase() || null;
          }
        } catch (_) {}
        return s.replace(/^@/, "").replace(/\s+/g, "").toLowerCase();
      })
      .filter(Boolean);

  const checkLive = async () => {
    setLoading(true);

    const list = normalizeChannels(channels).slice(0, maxCards);

    const checks = list.map(
      (ch) =>
        new Promise((resolve) => {
          const img = new Image();
          // probar 2 tamaños por si alguno 404 pero el otro no
          const base = `https://static-cdn.jtvnw.net/previews-ttv/live_user_${encodeURIComponent(
            ch
          )}`;
          const candidates = [
            `${base}-320x180.jpg?cb=${Date.now()}`,
            `${base}-440x248.jpg?cb=${Date.now()}`,
          ];

          let done = false;

          const tryNext = (idx) => {
            if (idx >= candidates.length) {
              if (!done) resolve({ channel: ch, ok: false });
              return;
            }
            img.onload = () => {
              if (!done) {
                done = true;
                resolve({ channel: ch, ok: true, url: candidates[idx] });
              }
            };
            img.onerror = () => tryNext(idx + 1);
            img.src = candidates[idx];
          };

          tryNext(0);

          // timeout de seguridad
          setTimeout(() => {
            if (!done) resolve({ channel: ch, ok: false });
          }, 5000);
        })
    );

    const results = await Promise.all(checks);
    const lives = results
      .filter((r) => r.ok)
      .map((r) => ({ channel: r.channel, thumbUrl: r.url }));

    setLiveList(lives);
    setLoading(false);
  };

  useEffect(() => {
    if (!channels?.length) {
      setLiveList([]);
      setLoading(false);
      return;
    }
    checkLive();
    timerRef.current = setInterval(checkLive, pollIntervalMs);
    return () => clearInterval(timerRef.current);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [JSON.stringify(channels), pollIntervalMs]);

  if (!loading && liveList.length === 0) return null;

  return (
    <aside style={styles.sidebar}>
      {loading && liveList.length === 0 ? (
        <div style={styles.skeletonBox}>
          <div style={styles.livePillMuted}>Buscando LIVE…</div>
        </div>
      ) : (
        liveList.map((s) => (
          <a
            key={s.channel}
            href={`https://www.twitch.tv/${encodeURIComponent(s.channel)}`}
            target="_blank"
            rel="noopener noreferrer"
            style={{ textDecoration: "none" }}
          >
            <div style={styles.card}>
              <div style={styles.livePill}>LIVE</div>
              <img
                src={s.thumbUrl}
                alt={`${s.channel} live preview`}
                style={{ width: "100%", height: 200, objectFit: "cover", display: "block" }}
              />
              <div style={styles.meta}>
                <div style={styles.streamTitle}>{s.channel}</div>
                <div style={styles.streamSub}>Click para abrir en Twitch</div>
              </div>
            </div>
          </a>
        ))
      )}
    </aside>
  );
}

const styles = {
  sidebar: { width: 360, minWidth: 320, display: "flex", flexDirection: "column", gap: 20 },
  card: {
    position: "relative",
    border: "3px solid #000",
    borderRadius: 10,
    background: "#f7f9fc",
    overflow: "hidden",
  },
  skeletonBox: {
    border: "3px solid #000",
    borderRadius: 10,
    background: "#f7f9fc",
    height: 200,
    display: "flex",
    alignItems: "flex-start",
  },
  livePill: {
    position: "absolute",
    top: 8,
    left: 8,
    background: "#ff3b30",
    color: "#fff",
    fontSize: 12,
    padding: "2px 8px",
    borderRadius: 999,
    fontWeight: 700,
    letterSpacing: 0.5,
    zIndex: 2,
  },
  livePillMuted: {
    margin: 8,
    background: "#e0e0e0",
    color: "#555",
    fontSize: 12,
    padding: "2px 8px",
    borderRadius: 999,
    fontWeight: 600,
    letterSpacing: 0.5,
  },
  meta: { padding: "10px 12px 12px" },
  streamTitle: { fontWeight: 700, color: "#1e2a38", marginBottom: 4, textTransform: "capitalize" },
  streamSub: { fontSize: 12, color: "#66788a" },
};
