import React from "react";
import { useDrop } from "react-dnd";

const CardSlot = ({ card, onAddCharacter, onUpdateCardData, onRemoveCharacter, onDeleteCard }) => {
  const [{ isOver }, drop] = useDrop(() => ({
    accept: "character",
    drop: (item) => onAddCharacter(card.id, item),
    collect: (monitor) => ({
      isOver: !!monitor.isOver(),
    }),
  }));

  const handleDateChange = (e) => {
    onUpdateCardData(card.id, { date: e.target.value });
  };

  const handleTimeChange = (e) => {
    onUpdateCardData(card.id, { time: e.target.value });
  };

  return (
    <div
      ref={drop}
      style={{
        border: `2px dashed ${isOver ? "#4CAF50" : "#ccc"}`,
        padding: 15,
        marginBottom: 20,
        borderRadius: 8,
        backgroundColor: isOver ? "#f0fff0" : "#fff",
        minHeight: 150,
        position: "relative",
      }}
    >
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h3 style={{ margin: 0 }}>{card.title}</h3>
        <button
          onClick={() => onDeleteCard(card.id)}
          style={{
            background: "#ff4d4d",
            color: "white",
            border: "none",
            borderRadius: 4,
            padding: "5px 10px",
            cursor: "pointer",
          }}
        >
          Eliminar
        </button>
      </div>

      <div style={{ display: "flex", gap: 10, margin: "10px 0" }}>
        <div>
          <label>Fecha: </label>
          <input type="date" value={card.date || ""} onChange={handleDateChange} style={{ padding: 5 }} />
        </div>
        <div>
          <label>Hora: </label>
          <input type="time" value={card.time || ""} onChange={handleTimeChange} style={{ padding: 5 }} />
        </div>
      </div>

      <div style={{ marginTop: 10 }}>
        <strong>Personajes ({card.characters.length}/8):</strong>
        <div style={{ display: "flex", flexWrap: "wrap", gap: 5, marginTop: 5 }}>
          {card.characters.map((character, idx) => (
            <div
              key={idx}
              style={{
                background: "#e0f7fa",
                padding: "5px 10px",
                borderRadius: 4,
                display: "flex",
                alignItems: "center",
              }}
            >
              <span>{character.name}</span>
              <button
                onClick={() => onRemoveCharacter(card.id, idx)}
                style={{
                  background: "none",
                  border: "none",
                  color: "#ff4d4d",
                  cursor: "pointer",
                  marginLeft: 5,
                }}
              >
                Ã—
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CardSlot;
