import React, { useEffect, useMemo, useRef, useState } from "react";

// Canales por defecto (puedes pasarlos por props)
const CHANNELS = [
  "https://www.twitch.tv/trickmasteh",
  "https://www.twitch.tv/blawbberto",
  "https://www.twitch.tv/kaosniight",
  "https://www.twitch.tv/khangsoofly",
  "https://www.twitch.tv/faaaxy",
  "https://www.twitch.tv/takeshijoga",
];

/**
 * Renderiza SOLO las tarjetas de canales que están LIVE.
 * Usa un probe del backend para evitar 404 en el navegador (mejora Best Practices).
 * Miniaturas angostas (cardWidth), relación 16:9, lazy + tamaños explícitos.
 * Pausa el polling cuando la pestaña está oculta y arranca en idle.
 */
export default function Streamings({
  channels = CHANNELS,
  pollIntervalMs = 120000,
  maxCards = 12,
  cardWidth = 220, // ancho de la tarjeta (angosta)
  probeEndpoint = "/api/stream/live-status",
}) {
  const [liveList, setLiveList] = useState([]); // [{channel, thumbUrl, w, h}]
  const [loading, setLoading] = useState(true);
  const timerRef = useRef(null);

  // Preconnect (mejora TTFB/cold start del CDN)
  useEffect(() => {
    const add = (href) => {
      const link = document.createElement("link");
      link.rel = "preconnect";
      link.href = href;
      link.crossOrigin = "";
      document.head.appendChild(link);
      return link;
    };
    const a = add("https://static-cdn.jtvnw.net");
    const b = add("https://www.twitch.tv");
    return () => {
      a.remove();
      b.remove();
    };
  }, []);

  const normalized = useMemo(
    () =>
      (channels || [])
        .map((raw) => {
          if (!raw) return null;
          const s = String(raw).trim();
          try {
            if (s.startsWith("http")) {
              const u = new URL(s);
              const parts = u.pathname.split("/").filter(Boolean);
              return parts[0]?.toLowerCase() || null;
            }
          } catch {}
          return s.replace(/^@/, "").replace(/\s+/g, "").toLowerCase();
        })
        .filter(Boolean)
        .slice(0, maxCards),
    [channels, maxCards]
  );

  const checkLive = async (signal) => {
    if (!normalized.length) {
      setLiveList([]);
      setLoading(false);
      return;
    }

    setLoading(true);
    const qs = (ch) => `${probeEndpoint}?channel=${encodeURIComponent(ch)}`;

    const res = await Promise.allSettled(
      normalized.map((c) => fetch(qs(c), { cache: "no-store", signal }))
    );
    const jsons = await Promise.all(
      res.map((r) => (r.status === "fulfilled" ? r.value.json().catch(() => null) : null))
    );

    const lives = (jsons || [])
      .filter((j) => j && j.live)
      .map((j) => ({
        channel: j.channel,
        // 320x180 es suficiente para 220px de ancho → menos bytes
        thumbUrl: `https://static-cdn.jtvnw.net/previews-ttv/live_user_${encodeURIComponent(
          j.channel
        )}-320x180.jpg?cb=${Date.now()}`,
        w: 320,
        h: 180,
      }));

    setLiveList(lives);
    setLoading(false);
  };

  // Arranca en idle y pausa en background
  useEffect(() => {
    const ctrl = new AbortController();

    const start = () => {
      if (document.visibilityState === "visible") checkLive(ctrl.signal);
    };

    // requestIdleCallback para no bloquear el primer render
    const ric = (window.requestIdleCallback ||
      function (cb) {
        return setTimeout(cb, 500);
      })(start);

    timerRef.current = setInterval(() => {
      if (document.visibilityState === "visible") checkLive(ctrl.signal);
    }, pollIntervalMs);

    const vis = () => document.visibilityState === "visible" && checkLive(ctrl.signal);
    document.addEventListener("visibilitychange", vis);

    return () => {
      (window.cancelIdleCallback ? window.cancelIdleCallback(ric) : clearTimeout(ric));
      clearInterval(timerRef.current);
      document.removeEventListener("visibilitychange", vis);
      ctrl.abort();
    };
  }, [normalized, pollIntervalMs]); // eslint-disable-line react-hooks/exhaustive-deps

  const cardHeight = Math.round((cardWidth * 9) / 16);

  return (
    <div
      aria-label="Listado de streamings en vivo"
      style={{
        display: "flex",
        flexDirection: "column",
        gap: 12,
        alignItems: "flex-end", // pega a la derecha
      }}
    >
      {loading && liveList.length === 0 ? (
        <div style={{ ...styles.skeletonBox, width: cardWidth, height: cardHeight }}>
          <div style={styles.livePillMuted}>Buscando LIVE…</div>
        </div>
      ) : (
        liveList.map((s) => (
          <a
            key={s.channel}
            href={`https://www.twitch.tv/${encodeURIComponent(s.channel)}`}
            target="_blank"
            rel="noopener noreferrer"
            style={{ textDecoration: "none" }}
          >
            <div
              style={{
                ...styles.card,
                width: cardWidth,
                contentVisibility: "auto",
                containIntrinsicSize: `${cardHeight + 48}px`,
                marginLeft: "auto",
              }}
            >
              <div style={styles.livePill}>LIVE</div>

              <img
                src={s.thumbUrl}
                srcSet={`${s.thumbUrl} ${s.w}w`}
                sizes={`${cardWidth}px`}
                alt={`${s.channel} en vivo`}
                width={cardWidth}
                height={cardHeight}
                loading="lazy"
                decoding="async"
                fetchpriority="low"
                style={{
                  width: "100%",
                  height: cardHeight,
                  objectFit: "cover",
                  display: "block",
                  background: "#555",
                }}
              />

              <div style={styles.meta}>
                <div style={styles.streamTitle}>{s.channel}</div>
                <div style={styles.streamSub}>Click para abrir en Twitch</div>
              </div>
            </div>
          </a>
        ))
      )}
    </div>
  );
}

const styles = {
  card: {
    position: "relative",
    border: "3px solid #000",
    borderRadius: 10,
    background: "#f7f9fc",
    overflow: "hidden",
  },
  skeletonBox: {
    border: "3px solid #000",
    borderRadius: 10,
    background: "#f7f9fc",
    display: "flex",
    alignItems: "flex-start",
    marginLeft: "auto",
  },
  livePill: {
    position: "absolute",
    top: 8,
    left: 8,
    background: "#ff3b30",
    color: "#fff",
    fontSize: 12,
    padding: "2px 8px",
    borderRadius: 999,
    fontWeight: 700,
    letterSpacing: 0.5,
    zIndex: 2,
  },
  livePillMuted: {
    margin: 8,
    background: "#e0e0e0",
    color: "#555",
    fontSize: 12,
    padding: "2px 8px",
    borderRadius: 999,
    fontWeight: 600,
    letterSpacing: 0.5,
  },
  meta: { padding: "8px 10px 10px" },
  streamTitle: { fontWeight: 700, color: "#1e2a38", marginBottom: 2, textTransform: "capitalize", fontSize: 14 },
  streamSub: { fontSize: 11, color: "#66788a" },
};
