import React, { useState, useEffect, useRef } from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import { fetchAppData, saveUserData, saveCards } from "./api/backendApi";
import LoginForm from "./components/LoginForm";
import UserProfile from "./components/UserProfile";
import Header from "./components/Header";
import HomePage from "./components/HomePage";
import AdminPanel from "./components/AdminPanel";

function App() {
  const [userEmail, setUserEmail] = useState(null);
  const [userData, setUserData] = useState(null);
  const [appData, setAppData] = useState({
    users: {},
    cards: [],
    allCharacters: [],
  });
  const [loading, setLoading] = useState(true);
  const [loginMode, setLoginMode] = useState("login"); // puede ser "login", "register" o "createPassword"
  const isLoggingOut = useRef(false);

  useEffect(() => {
    const loadData = async () => {
      try {
        const data = await fetchAppData();
        setAppData({
          users: data.users || {},
          cards: data.cards || [],
          allCharacters: getAllCharacters(data.users),
        });
      } catch (err) {
        console.error("Error loading data:", err);
      } finally {
        setLoading(false);
      }
    };
    loadData();
  }, []);

  useEffect(() => {
    if (isLoggingOut.current) return;
    if (userEmail && userData) {
      const saveTimeout = setTimeout(() => {
        saveUserData(userEmail, userData)
          .then(() => {
            setAppData((prev) => ({
              ...prev,
              users: {
                ...prev.users,
                [userEmail]: userData,
              },
              allCharacters: getAllCharacters({
                ...prev.users,
                [userEmail]: userData,
              }),
            }));
          })
          .catch((error) => {
            console.error("Save user error:", error);
            alert("Error al guardar los datos. Intente nuevamente.");
          });
      }, 500);

      return () => clearTimeout(saveTimeout);
    }
  }, [userData, userEmail]);

  const getAllCharacters = (users) => {
    const characters = [];
    Object.entries(users).forEach(([email, userData]) => {
      if (userData.characters) {
        userData.characters.forEach((char) => {
          characters.push({
            ...char,
            userEmail: email,
          });
        });
      }
    });
    return characters;
  };

  const saveCardsHandler = async (cards) => {
    try {
      await saveCards(cards);
      setAppData((prev) => ({ ...prev, cards }));
    } catch (error) {
      console.error("Save cards error:", error);
      alert("Error al guardar las tarjetas. Intente nuevamente.");
    }
  };

  const handleLogin = async (email, password, mode) => {
    const trimmedEmail = email.trim().toLowerCase();
    const existingUser = appData.users[trimmedEmail];

    if (mode === "register") {
      if (existingUser) throw new Error("Usuario ya registrado");

      const newUser = {
        password,
        characters: [],
        labels: [],
        role: "user",
      };

      setUserEmail(trimmedEmail);
      setUserData(newUser);
      setLoginMode("login");
      return newUser;
    }

    if (mode === "createPassword") {
      if (!existingUser) throw new Error("Usuario no encontrado");
      if (existingUser.password && existingUser.password !== "") {
        throw new Error("La contraseña ya fue creada");
      }
      // Asumimos que backend actualizó la contraseña correctamente
      setUserEmail(trimmedEmail);
      setUserData({ ...existingUser, password });
      setLoginMode("login");
      return existingUser;
    }

    // login normal
    if (!existingUser) throw new Error("Usuario no encontrado");

    if (existingUser.role === "admin" && (!existingUser.password || existingUser.password === "")) {
      // Indicar al frontend que debe crear contraseña
      throw new Error("Debe crear contraseña para su usuario");
    }

    if (existingUser.password !== password) {
      throw new Error("Credenciales inválidas");
    }

    setUserEmail(trimmedEmail);
    setUserData({
      ...existingUser,
      role: existingUser.role || "user",
    });
    setLoginMode("login");
    return existingUser;
  };

  const handleLogout = () => {
    isLoggingOut.current = true;

    if (userEmail && userData) {
      saveUserData(userEmail, userData).catch((error) => {
        console.error("Final save error:", error);
      });
    }

    setUserEmail(null);
    setUserData(null);
    setLoginMode("login");

    setTimeout(() => {
      isLoggingOut.current = false;
    }, 1000);
  };

  const userRole = userData?.role || "user";

  return (
    <BrowserRouter>
      <Header userEmail={userEmail} onLogout={handleLogout} />
      <Routes>
        <Route path="/" element={<HomePage cards={appData.cards} loading={loading} />} />
        <Route
          path="/login"
          element={
            userEmail ? (
              <Navigate to={userRole === "admin" ? "/admin" : "/profile"} />
            ) : (
              <LoginForm onLogin={handleLogin} mode={loginMode} />
            )
          }
        />
        <Route
          path="/profile"
          element={
            userEmail && userRole === "user" ? (
              <UserProfile userEmail={userEmail} userData={userData} setUserData={setUserData} />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route
          path="/admin"
          element={
            userEmail && userRole === "admin" ? (
              <AdminPanel
                cards={appData.cards}
                allCharacters={appData.allCharacters}
                onSaveCards={saveCardsHandler}
              />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
