import React from "react";
import { Link } from "react-router-dom";

// ‚ö° Carga diferida: reduce JS inicial y mejora TBT/LCP
const StreamingsLazy = React.lazy(() => import("./Streamings"));

// Normalizamos para que siempre caigan al final sin importar may√∫sculas/espacios
const SPECIAL_TAIL = new Set(["bard", "paladin", "artist", "valkyrie"]);
const isTailClass = (clsName) =>
  typeof clsName === "string" && SPECIAL_TAIL.has(clsName.trim().toLowerCase());

// Ordena: primero NO especiales, al final especiales (orden estable)
const sortTeam = (arr = []) => {
  const withIndex = arr.map((c, i) => ({ c, i }));
  withIndex.sort((a, b) => {
    const aIsTail = isTailClass(a.c?.class?.name);
    const bIsTail = isTailClass(b.c?.class?.name);
    if (aIsTail !== bIsTail) return aIsTail ? 1 : -1; // especiales al final
    return a.i - b.i; // orden estable dentro del grupo
  });
  return withIndex.map((x) => x.c);
};

// ---- Helpers de fecha/hora ----
const mmddRe = /^(0[1-9]|1[0-2])-(0[1-9]|[12]\d|3[01])$/;
const ymdRe = /^(\d{4})-(0[1-9]|1[0-2])-(0[1-9]|[12]\d|3[01])$/;

// Formatea a MM-DD (solo mes y d√≠a), soporta YYYY-MM-DD y MM-DD
const formatDayMonth = (value) => {
  if (!value) return "Fecha no asignada";
  const s = String(value).trim();

  if (mmddRe.test(s)) {
    const [m, d] = s.split("-");
    return `${m.padStart(2, "0")}-${d.padStart(2, "0")}`;
  }

  const ymd = s.match(ymdRe);
  if (ymd) {
    const [, , m, d] = ymd;
    return `${m}-${d}`;
  }

  const d = new Date(s);
  if (!isNaN(d)) {
    const mm = String(d.getMonth() + 1).padStart(2, "0");
    const dd = String(d.getDate()).padStart(2, "0");
    return `${mm}-${dd}`;
  }

  return s;
};

// Clave de orden para la fecha (ignorando a√±o). MMDD como n√∫mero.
const dateKeyFromDate = (dateStr) => {
  if (!dateStr) return Number.POSITIVE_INFINITY;
  const s = String(dateStr).trim();

  if (mmddRe.test(s)) {
    const [m, d] = s.split("-");
    return parseInt(`${m}${d}`, 10);
  }
  const ymd = s.match(ymdRe);
  if (ymd) {
    const [, , m, d] = ymd;
    return parseInt(`${m}${d}`, 10);
  }
  const d = new Date(s);
  if (!isNaN(d)) {
    const mm = String(d.getMonth() + 1).padStart(2, "0");
    const dd = String(d.getDate()).padStart(2, "0");
    return parseInt(`${mm}${dd}`, 10);
  }
  return Number.POSITIVE_INFINITY;
};

// Minutos desde "00:00". Vac√≠os/ inv√°lidos => +‚àû (al final).
const minutesFromTime = (timeStr) => {
  if (!timeStr) return Number.POSITIVE_INFINITY;
  const s = String(timeStr).trim();

  // "HH:MM"
  let m = s.match(/^([01]?\d|2[0-3]):([0-5]\d)$/);
  if (m) return parseInt(m[1], 10) * 60 + parseInt(m[2], 10);

  // "HHMM"
  m = s.match(/^(\d{3,4})$/);
  if (m) {
    const raw = m[1];
    const hh = parseInt(raw.length === 3 ? raw.slice(0, 1) : raw.slice(0, 2), 10);
    const mm = parseInt(raw.length === 3 ? raw.slice(1) : raw.slice(2), 10);
    if (hh >= 0 && hh <= 23 && mm >= 0 && mm <= 59) return hh * 60 + mm;
  }

  // "HH"
  m = s.match(/^([01]?\d|2[0-3])$/);
  if (m) return parseInt(m[1], 10) * 60;

  return Number.POSITIVE_INFINITY;
};

// Construye un Date a partir de {date, time}. Para MM-DD usa el a√±o actual.
const dateFromCard = (dateStr, timeStr) => {
  if (!dateStr) return null;

  const now = new Date();
  let y = now.getFullYear(),
    m,
    d;

  if (mmddRe.test(dateStr)) {
    const [mm, dd] = dateStr.split("-").map((x) => parseInt(x, 10));
    m = mm;
    d = dd;
  } else {
    const ymd = dateStr.match(ymdRe);
    if (ymd) {
      y = parseInt(ymd[1], 10);
      m = parseInt(ymd[2], 10);
      d = parseInt(ymd[3], 10);
    } else {
      const free = new Date(dateStr);
      if (isNaN(free)) return null;
      y = free.getFullYear();
      m = free.getMonth() + 1;
      d = free.getDate();
    }
  }

  let hh = 0,
    mm = 0;
  if (typeof timeStr === "string" && timeStr.trim()) {
    const s = timeStr.trim();
    let m1 = s.match(/^([01]?\d|2[0-3]):([0-5]\d)$/);
    if (m1) {
      hh = parseInt(m1[1], 10);
      mm = parseInt(m1[2], 10);
    } else {
      m1 = s.match(/^(\d{3,4})$/);
      if (m1) {
        const raw = m1[1];
        hh = parseInt(raw.length === 3 ? raw.slice(0, 1) : raw.slice(0, 2), 10);
        mm = parseInt(raw.length === 3 ? raw.slice(1) : raw.slice(2), 10);
      } else {
        m1 = s.match(/^([01]?\d|2[0-3])$/);
        if (m1) hh = parseInt(m1[1], 10);
      }
    }
  }

  const dt = new Date(y, (m || 1) - 1, d || 1, hh, mm, 0, 0);
  return isNaN(dt) ? null : dt;
};

// Estado de una card: live/expired/upcoming
const getCardStatus = (card, nowTs) => {
  const start = dateFromCard(card?.date, card?.time);
  if (!start) return "upcoming"; // sin fecha: nunca glow ni se oculta

  const startTs = start.getTime();
  const endTs = startTs + 60 * 60 * 1000; // +1h
  if (nowTs >= endTs) return "expired";
  if (nowTs >= startTs) return "live";
  return "upcoming";
};

const CharItem = React.memo(function CharItem({ char }) {
  if (!char) return null;
  return (
    <div
      style={{
        padding: "10px",
        borderRadius: "10px",
        backgroundColor: "#f8fafc",
        border: "1px solid #e2e8f0",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        gap: 6,
        textAlign: "center",
        contentVisibility: "auto",
        containIntrinsicSize: "60px",
      }}
      title={char.name}
    >
      {char.class?.image ? (
        <img
          src={char.class.image}
          alt={char.class?.name || "Clase"}
          width={32}
          height={32}
          style={{ width: 32, height: 32, borderRadius: 8, objectFit: "cover" }}
          loading="lazy"
          decoding="async"
        />
      ) : (
        <div
          aria-hidden
          style={{
            width: 32,
            height: 32,
            borderRadius: 8,
            background: "#e5e7eb",
            display: "grid",
            placeItems: "center",
            fontSize: 12,
            color: "#6b7280",
          }}
        >
          ?
        </div>
      )}

      <div
        style={{
          fontWeight: 700,
          fontSize: 13,
          color: "#0f172a",
          whiteSpace: "nowrap",
          overflow: "hidden",
          textOverflow: "ellipsis",
          width: "100%",
        }}
      >
        {char.name}
      </div>
    </div>
  );
});

// Carga Streamings s√≥lo cuando es visible en viewport y reporta si hay LIVE
function LazyStreamingsContainer({ onHasLiveChange }) {
  const [visible, setVisible] = React.useState(false);
  const ref = React.useRef(null);

  React.useEffect(() => {
    const el = ref.current;
    if (!el) return;
    const io = new IntersectionObserver(
      (entries) => {
        if (entries.some((e) => e.isIntersecting)) {
          setVisible(true);
          io.disconnect();
        }
      },
      { rootMargin: "0px 0px 200px 0px" }
    );
    io.observe(el);
    return () => io.disconnect();
  }, []);

  return (
    <div ref={ref} style={{ minHeight: 1 }}>
      {visible ? (
        <React.Suspense fallback={null}>
          <StreamingsLazy
            pollIntervalMs={120000}
            cardWidth={220}
            onLiveCountChange={(count) => onHasLiveChange?.(count > 0)}
          />
        </React.Suspense>
      ) : null}
    </div>
  );
}

export default function HomePage({ cards = [], loading, error }) {
  // Ticker para recalcular "live/expired" sin recargar
  const [nowTs, setNowTs] = React.useState(() => Date.now());
  const [hasLive, setHasLive] = React.useState(false); // üëâ controla visibilidad del panel
  React.useEffect(() => {
    const id = setInterval(() => setNowTs(Date.now()), 30_000); // cada 30s
    return () => clearInterval(id);
  }, []);

  // Ordenar tarjetas
  const sortedCards = React.useMemo(() => {
    return [...cards].sort((a, b) => {
      const da = dateKeyFromDate(a?.date);
      const db = dateKeyFromDate(b?.date);
      if (da !== db) return da - db;

      const ta = minutesFromTime(a?.time);
      const tb = minutesFromTime(b?.time);
      if (ta !== tb) return ta - tb;

      const at = (a?.title || "").toLowerCase();
      const bt = (b?.title || "").toLowerCase();
      if (at < bt) return -1;
      if (at > bt) return 1;
      return 0;
    });
  }, [cards]);

  // Filtrar: ocultar expiradas
  const visibleCards = React.useMemo(
    () => sortedCards.filter((c) => getCardStatus(c, nowTs) !== "expired"),
    [sortedCards, nowTs]
  );

  return (
    <div
      style={{
        maxWidth: "1480px",
        margin: "20px auto",
        padding: "20px",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        color: "#34495e",
      }}
    >
      <style>{`
        @keyframes whaleGlowPulse {
          0% { box-shadow: 0 0 0 0 rgba(30,136,229,0.35), 0 0 24px rgba(30,136,229,0.45); }
          50% { box-shadow: 0 0 0 6px rgba(30,136,229,0.12), 0 0 36px rgba(30,136,229,0.65); }
          100% { box-shadow: 0 0 0 0 rgba(30,136,229,0.35), 0 0 24px rgba(30,136,229,0.45); }
        }
        .whale-glow { animation: whaleGlowPulse 1.6s ease-in-out infinite; border-radius: 14px; }

        /* Izquierda flexible, sidebar fija a la derecha */
        .wc-sections {
          display: grid;
          grid-template-columns: minmax(0, 1fr) 360px;
          gap: 24px;
          align-items: start;
        }
        @media (max-width: 1100px) {
          .wc-sections { grid-template-columns: 1fr; }
        }

        /* Acelera render fuera de viewport */
        .wc-left { content-visibility: auto; contain-intrinsic-size: 600px; }
        .wc-right { content-visibility: auto; contain-intrinsic-size: 400px; }
      `}</style>

      {/* SECTION: Bienvenida */}
      <section
        aria-labelledby="welcome-title"
        style={{
          textAlign: "center",
          marginBottom: "28px",
          padding: "20px",
          backgroundColor: "#f7f9fc",
          borderRadius: "10px",
          boxShadow: "0 4px 10px rgba(0,0,0,0.05)",
          display: transparent
        }}
      >
        <h1 id="welcome-title" style={{ color: "#1e88e5", fontWeight: 700, margin: 0 }}>
          Bienvenido a WhaleCorp
        </h1>
        <p style={{ fontSize: 18, maxWidth: 700, margin: "8px auto 0", color: "#66788a" }}>
          A page made with love ‚ô•
        </p>
      </section>

      {/* ESTADOS */}
      {loading ? (
        <section style={{ textAlign: "center", margin: "50px 0" }}>
          <div className="spinner" />
          <p style={{ color: "#95a5a6", fontSize: 16 }}>Cargando personajes...</p>
        </section>
      ) : error ? (
        <section style={{ textAlign: "center", margin: "50px 0", color: "#e74c3c" }}>
          <p>Error al cargar los datos: {error}</p>
          <button
            onClick={() => window.location.reload()}
            style={{
              padding: "10px 20px",
              background: "#1e88e5",
              color: "white",
              border: "none",
              borderRadius: 6,
              cursor: "pointer",
              fontWeight: 600,
              fontSize: 16,
              marginTop: 15,
              boxShadow: "0 2px 6px rgba(30,136,229,0.4)",
            }}
            onMouseOver={(e) => (e.currentTarget.style.backgroundColor = "#1565c0")}
            onMouseOut={(e) => (e.currentTarget.style.backgroundColor = "#1e88e5")}
          >
            Reintentar
          </button>
        </section>
      ) : visibleCards.length === 0 ? (
        <section style={{ textAlign: "center", margin: "50px 0", color: "#7f8c8d", fontSize: 18 }}>
          <p>No hay tarjetas disponibles</p>
          <Link
            to="/login"
            style={{
              display: "inline-block",
              marginTop: 20,
              padding: "10px 25px",
              background: "#1e88e5",
              color: "white",
              textDecoration: "none",
              borderRadius: 8,
              fontWeight: 600,
              boxShadow: "0 2px 6px rgba(30,136,229,0.4)",
            }}
            onMouseOver={(e) => (e.currentTarget.style.backgroundColor = "#1565c0")}
            onMouseOut={(e) => (e.currentTarget.style.backgroundColor = "#1e88e5")}
          >
            Inicia sesi√≥n como administrador
          </Link>
        </section>
      ) : (
        <section className="wc-sections" aria-label="contenido principal">
          {/* Pending Raids (izquierda) */}
          <section aria-labelledby="raids-title" className="wc-left">
            <h2
              id="raids-title"
              style={{
                textAlign: "center",
                marginBottom: "24px",
                color: "#2c3e50",
                fontWeight: 700,
                letterSpacing: "1px",
              }}
            >
              Pending Raids
            </h2>

            <div
              style={{
                display: "grid",
                gridTemplateColumns: "repeat(3, 1fr)",
                gap: "24px",
              }}
            >
              {visibleCards.map((card, index) => {
                const status = getCardStatus(card, nowTs);
                const isLive = status === "live";

                const team1Raw = Array.isArray(card.team1)
                  ? card.team1
                  : Array.isArray(card.characters)
                  ? card.characters.slice(0, 4)
                  : [];
                const team2Raw = Array.isArray(card.team2)
                  ? card.team2
                  : Array.isArray(card.characters)
                  ? card.characters.slice(4, 8)
                  : [];

                const team1 = sortTeam(team1Raw).slice(0, 4);
                const team2 = sortTeam(team2Raw).slice(0, 4);
                const hasAny = team1.length + team2.length > 0;

                return (
                  <div
                    key={index}
                    className={isLive ? "whale-glow" : undefined} // glow intacto
                    style={{
                      borderRadius: 14,
                      overflow: "hidden",
                      boxShadow: isLive
                        ? "0 0 0 0 rgba(30,136,229,0.35), 0 0 24px rgba(30,136,229,0.55)"
                        : "0 10px 20px rgba(0,0,0,0.08)",
                      backgroundColor: "#fff",
                      display: "flex",
                      flexDirection: "column",
                      transition: "transform 0.25s ease, box-shadow 0.25s ease",
                    }}
                    onMouseEnter={(e) => {
                      if (!isLive) {
                        e.currentTarget.style.transform = "translateY(-6px)";
                        e.currentTarget.style.boxShadow =
                          "0 20px 40px rgba(0,0,0,0.12)";
                      }
                    }}
                    onMouseLeave={(e) => {
                      if (!isLive) {
                        e.currentTarget.style.transform = "translateY(0)";
                        e.currentTarget.style.boxShadow =
                          "0 10px 20px rgba(0,0,0,0.08)";
                      }
                    }}
                  >
                    {/* Header */}
                    <div
                      style={{
                        padding: "18px 22px",
                        backgroundColor: "#e3f2fd",
                        borderBottom: "1px solid #bbdefb",
                        userSelect: "none",
                      }}
                    >
                      <h3
                        style={{
                          margin: 0,
                          color: "#1565c0",
                          fontWeight: 700,
                          fontSize: "1.3rem",
                          lineHeight: 1.15,
                          textOverflow: "ellipsis",
                          whiteSpace: "nowrap",
                          overflow: "hidden",
                        }}
                        title={card.title}
                      >
                        {card.title}
                      </h3>
                      <div
                        style={{
                          display: "flex",
                          justifyContent: "space-between",
                          fontSize: 14,
                          color: "#607d8b",
                          marginTop: 6,
                          fontWeight: 600,
                        }}
                      >
                        <span>
                          {card.date ? formatDayMonth(card.date) : "Fecha no asignada"}
                        </span>
                        <span>{card.time || "Hora no asignada"}</span>
                      </div>
                    </div>

                    {/* Body */}
                    <div
                      style={{
                        padding: "18px 22px",
                        display: "grid",
                        gridTemplateColumns: "1fr 1fr",
                        gap: 16,
                      }}
                    >
                      {!hasAny ? (
                        <p
                          style={{
                            color: "#90a4ae",
                            textAlign: "center",
                            padding: "30px 0",
                            gridColumn: "1 / -1",
                            fontWeight: 500,
                            fontStyle: "italic",
                            margin: 0,
                          }}
                        >
                          Tarjeta pendiente de asignaci√≥n
                        </p>
                      ) : (
                        <>
                          <div>
                            <div
                              style={{
                                display: "flex",
                                justifyContent: "space-between",
                                alignItems: "center",
                                marginBottom: 8,
                              }}
                            >
                              <strong style={{ color: "#0d47a1" }}>Party 1</strong>
                              <span style={{ fontSize: 12, color: "#64748b" }}>
                                {team1.length}/4
                              </span>
                            </div>
                            <div
                              style={{
                                display: "grid",
                                gridTemplateRows: "repeat(4, minmax(0, auto))",
                                gap: 10,
                              }}
                            >
                              {team1.map((char, i) => (
                                <CharItem key={`t1-${i}-${char?.name || i}`} char={char} />
                              ))}
                            </div>
                          </div>

                          <div>
                            <div
                              style={{
                                display: "flex",
                                justifyContent: "space-between",
                                alignItems: "center",
                                marginBottom: 8,
                              }}
                            >
                              <strong style={{ color: "#0d47a1" }}>Party 2</strong>
                              <span style={{ fontSize: 12, color: "#64748b" }}>
                                {team2.length}/4
                              </span>
                            </div>
                            <div
                              style={{
                                display: "grid",
                                gridTemplateRows: "repeat(4, minmax(0, auto))",
                                gap: 10,
                              }}
                            >
                              {team2.map((char, i) => (
                                <CharItem key={`t2-${i}-${char?.name || i}`} char={char} />
                              ))}
                            </div>
                          </div>
                        </>
                      )}
                    </div>
                  </div>
                );
              })}
            </div>
          </section>

          {/* Live Streamings ‚Äî pegado a la derecha.
              El panel solo se muestra si existe al menos 1 stream LIVE. */}
          <section aria-labelledby="streams-title" className="wc-right" style={{ justifySelf: "end" }}>
            <h2
              id="streams-title"
              style={{
                color: "#2c3e50",
                fontWeight: 700,
                letterSpacing: "0.5px",
                margin: "0 0 12px",
                textAlign: "right",
              }}
            >
              Live Streamings
            </h2>

            {hasLive && (
              <div
                style={{
                  borderRadius: 10,
                  padding: 12,
                  background: "#f5f7fa",
                  width: 360,
                }}
              >
                <LazyStreamingsContainer onHasLiveChange={setHasLive} />
              </div>
            )}

            {/* Montamos un watcher invisible para detectar cuando aparezca un LIVE */}
            {!hasLive && (
              <div style={{ display: "none" }}>
                <LazyStreamingsContainer onHasLiveChange={setHasLive} />
              </div>
            )}
          </section>
        </section>
      )}
    </div>
  );
}
