// frontend/src/components/AdminPanel.jsx
import React, { useState, useMemo } from "react";
import Modal from "react-modal";
import Select from "react-select";

Modal.setAppElement("#root");

const AdminPanel = ({ cards, allCharacters, onSaveCards }) => {
  // Título para crear nueva tarjeta (solo header)
  const [createTitle, setCreateTitle] = useState("");

  // Estado del modal
  const [modalOpen, setModalOpen] = useState(false);
  const [editingCardId, setEditingCardId] = useState(null);

  // Campos dentro del modal (separados del header)
  const [modalTitle, setModalTitle] = useState("");
  const [selectedCharacters, setSelectedCharacters] = useState([]);
  const [selectedDate, setSelectedDate] = useState("");
  const [selectedTime, setSelectedTime] = useState("");

  // Opciones del selector: guardamos el OBJETO COMPLETO del personaje en value
  const options = useMemo(
    () =>
      allCharacters.map((char) => ({
        value: char,           // char completo {name, label, class, userEmail...}
        label: char.name,      // lo que se ve
      })),
    [allCharacters]
  );

  // Abrir modal (para crear o editar)
  const openModal = (card) => {
    setEditingCardId(card?.id || null);

    // Si edito, cargo título/fecha/hora/selección desde la tarjeta
    setModalTitle(card?.title || "");
    setSelectedCharacters(
      card
        ? card.characters.map((c) => ({
            value: c,        // conservar el objeto personaje
            label: c.name,
          }))
        : []
    );
    setSelectedDate(card?.date || "");
    setSelectedTime(card?.time || "");

    setModalOpen(true);
  };

  // Guardar en el modal
  const saveModal = () => {
    const trimmedTitle = modalTitle.trim() || createTitle.trim();

    if (!trimmedTitle) {
      alert("Debe ingresar un título para la tarjeta");
      return;
    }
    if (selectedCharacters.length === 0) {
      alert("Debe seleccionar al menos un personaje");
      return;
    }
    if (selectedCharacters.length > 8) {
      alert("No puedes seleccionar más de 8 personajes");
      return;
    }

    // Extraemos el objeto personaje completo
    const characters = selectedCharacters.map((o) => o.value);

    if (editingCardId) {
      // Editar tarjeta
      const updatedCards = cards.map((card) =>
        card.id === editingCardId
          ? {
              ...card,
              title: trimmedTitle,   // actualizar también el título
              characters,
              date: selectedDate,
              time: selectedTime,
            }
          : card
      );
      onSaveCards(updatedCards);
    } else {
      // Crear nueva tarjeta
      const newCard = {
        id: Date.now(),
        title: trimmedTitle,
        characters,
        date: selectedDate,
        time: selectedTime,
      };
      onSaveCards([...cards, newCard]);

      // limpiar input de creación
      setCreateTitle("");
    }

    setModalOpen(false);
  };

  // Eliminar tarjeta
  const deleteCard = (cardId) => {
    if (window.confirm("¿Seguro que quieres eliminar esta tarjeta?")) {
      onSaveCards(cards.filter((card) => card.id !== cardId));
    }
  };

  return (
    <div
      style={{
        padding: 20,
        maxWidth: 1100,
        margin: "0 auto",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        color: "#34495e",
      }}
    >
      <h1 style={{ marginBottom: 20, color: "#1e88e5", fontWeight: "700" }}>
        Panel de Administración
      </h1>

      {/* Header de creación (YA NO cambia cuando editas) */}
      <div style={{ marginBottom: 20, display: "flex", gap: 10 }}>
        <input
          type="text"
          placeholder="Título para nueva tarjeta"
          value={createTitle}
          onChange={(e) => setCreateTitle(e.target.value)}
          style={{
            flex: 1,
            padding: 10,
            borderRadius: 8,
            border: "1px solid #ccc",
            fontSize: "1rem",
            outline: "none",
          }}
        />
        <button
          onClick={() => {
            // Abrimos el modal en modo crear y precargamos el título con lo del header
            setEditingCardId(null);
            setModalTitle(createTitle);
            setSelectedCharacters([]);
            setSelectedDate("");
            setSelectedTime("");
            setModalOpen(true);
          }}
          style={{
            padding: "10px 20px",
            cursor: "pointer",
            backgroundColor: "#1e88e5",
            color: "white",
            border: "none",
            borderRadius: 8,
            fontWeight: "600",
            fontSize: "1rem",
            boxShadow: "0 3px 6px rgba(30,136,229,0.5)",
            transition: "background-color 0.3s ease",
          }}
          onMouseOver={(e) =>
            (e.currentTarget.style.backgroundColor = "#1565c0")
          }
          onMouseOut={(e) =>
            (e.currentTarget.style.backgroundColor = "#1e88e5")
          }
        >
          Crear Tarjeta
        </button>
      </div>

      {/* Grid de tarjetas */}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(5, 1fr)",
          gap: 24,
          justifyItems: "stretch",
        }}
      >
        {cards.length === 0 ? (
          <p>No hay tarjetas creadas</p>
        ) : (
          cards.map((card) => (
            <div
              key={card.id}
              style={{
                borderRadius: 12,
                boxShadow: "0 12px 20px rgba(0,0,0,0.08)",
                backgroundColor: "#f0f5ff",
                display: "flex",
                flexDirection: "column",
                padding: 12,
                justifyContent: "space-between",
                cursor: "default",
                width: "100%",
              }}
            >
              <div>
                <h3
                  style={{
                    color: "#1e88e5",
                    fontWeight: "700",
                    marginBottom: 6,
                    whiteSpace: "nowrap",
                    overflow: "hidden",
                    textOverflow: "ellipsis",
                  }}
                  title={card.title}
                >
                  {card.title}
                </h3>
                <p
                  style={{
                    color: "#607d8b",
                    fontWeight: "600",
                    fontSize: "0.9rem",
                    margin: 0,
                  }}
                >
                  {card.date || "No asignada"}
                </p>
                <p
                  style={{
                    color: "#607d8b",
                    fontWeight: "600",
                    fontSize: "0.9rem",
                    marginTop: 2,
                  }}
                >
                  {card.time || "No asignada"}
                </p>

                <div style={{ marginTop: 10 }}>
                  <strong
                    style={{
                      fontWeight: "700",
                      color: "#0d47a1",
                    }}
                  >
                    Personajes:
                  </strong>

                  <div
                    style={{
                      display: "flex",
                      flexWrap: "wrap",
                      gap: 6,
                      marginTop: 6,
                    }}
                  >
                    {card.characters.map((c, i) => (
                      <span
                        key={i}
                        style={{
                          display: "inline-flex",
                          alignItems: "center",
                          gap: 6,
                          backgroundColor: "#bbdefb",
                          padding: "5px 10px",
                          borderRadius: 20,
                          fontSize: "0.9em",
                          fontWeight: 600,
                          color: "#0d47a1",
                          userSelect: "none",
                          boxShadow: "0 1px 4px rgba(0,71,173,0.3)",
                        }}
                      >
                        {c.class?.image && (
                          <img
                            src={c.class.image}
                            alt={c.class.name}
                            style={{ width: 18, height: 18, borderRadius: "50%" }}
                          />
                        )}
                        {c.name}
                      </span>
                    ))}
                  </div>
                </div>
              </div>

              <div
                style={{
                  marginTop: 14,
                  display: "flex",
                  gap: 8,
                }}
              >
                <button
                  onClick={() => openModal(card)}
                  style={{
                    flex: 1,
                    padding: "8px 0",
                    cursor: "pointer",
                    borderRadius: 8,
                    border: "none",
                    backgroundColor: "#1976d2",
                    color: "white",
                    fontWeight: "600",
                    transition: "background-color 0.3s ease",
                  }}
                  onMouseOver={(e) =>
                    (e.currentTarget.style.backgroundColor = "#115293")
                  }
                  onMouseOut={(e) =>
                    (e.currentTarget.style.backgroundColor = "#1976d2")
                  }
                >
                  Editar Tarjeta
                </button>

                <button
                  onClick={() => deleteCard(card.id)}
                  style={{
                    flex: 1,
                    padding: "8px 0",
                    cursor: "pointer",
                    borderRadius: 8,
                    border: "none",
                    backgroundColor: "#d32f2f",
                    color: "white",
                    fontWeight: "600",
                    transition: "background-color 0.3s ease",
                  }}
                  onMouseOver={(e) =>
                    (e.currentTarget.style.backgroundColor = "#9a2424")
                  }
                  onMouseOut={(e) =>
                    (e.currentTarget.style.backgroundColor = "#d32f2f")
                  }
                >
                  Eliminar Tarjeta
                </button>
              </div>
            </div>
          ))
        )}
      </div>

      {/* MODAL */}
      <Modal
        isOpen={modalOpen}
        onRequestClose={() => setModalOpen(false)}
        contentLabel="Configurar Tarjeta"
        style={{
          content: {
            maxWidth: "600px",
            margin: "auto",
            height: "fit-content",
            padding: "20px",
            borderRadius: "12px",
            boxShadow: "0 10px 30px rgba(0,0,0,0.15)",
          },
        }}
      >
        <h2 style={{ marginBottom: 20 }}>
          {editingCardId ? "Editar Tarjeta" : "Nueva Tarjeta"}
        </h2>

        {/* Título siempre se edita aquí dentro del modal */}
        <input
          type="text"
          placeholder="Título para la tarjeta"
          value={modalTitle}
          onChange={(e) => setModalTitle(e.target.value)}
          style={{
            width: "100%",
            padding: 12,
            marginBottom: 20,
            borderRadius: 8,
            border: "1px solid #ccc",
            fontSize: "1rem",
            outline: "none",
          }}
        />

        <label style={{ display: "block", marginBottom: 10 }}>
          Fecha:
          <input
            type="date"
            value={selectedDate}
            onChange={(e) => setSelectedDate(e.target.value)}
            style={{
              width: "100%",
              padding: 10,
              marginTop: 6,
              borderRadius: 8,
              border: "1px solid #ccc",
              fontSize: "1rem",
            }}
          />
        </label>

        <label style={{ display: "block", marginBottom: 10 }}>
          Hora:
          <input
            type="time"
            value={selectedTime}
            onChange={(e) => setSelectedTime(e.target.value)}
            style={{
              width: "100%",
              padding: 10,
              marginTop: 6,
              borderRadius: 8,
              border: "1px solid #ccc",
              fontSize: "1rem",
            }}
          />
        </label>

        <label style={{ fontWeight: 600, marginBottom: 8, display: "block" }}>
          Selecciona personajes (máx 8):
        </label>
        <Select
          isMulti
          options={options}
          value={selectedCharacters}
          onChange={setSelectedCharacters}
          placeholder="Buscar personajes..."
          closeMenuOnSelect={false}
          styles={{
            control: (provided) => ({
              ...provided,
              borderRadius: 8,
              fontSize: "1rem",
              minHeight: "40px",
            }),
            multiValue: (provided) => ({
              ...provided,
              backgroundColor: "#bbdefb",
              borderRadius: "20px",
              padding: "2px 8px",
              fontWeight: "600",
              color: "#0d47a1",
            }),
            multiValueLabel: (provided) => ({
              ...provided,
              color: "#0d47a1",
              padding: 0,
              paddingLeft: 4,
              fontSize: "0.9rem",
            }),
            multiValueRemove: (provided) => ({
              ...provided,
              color: "#0d47a1",
              ":hover": {
                backgroundColor: "#0d47a1",
                color: "white",
              },
            }),
          }}
        />

        <div
          style={{
            marginTop: 24,
            display: "flex",
            justifyContent: "flex-end",
            gap: 12,
          }}
        >
          <button
            onClick={() => setModalOpen(false)}
            style={{
              padding: "10px 20px",
              borderRadius: 8,
              border: "1px solid #ccc",
              backgroundColor: "white",
              cursor: "pointer",
              fontWeight: "600",
              fontSize: "1rem",
            }}
          >
            Cancelar
          </button>
          <button
            onClick={saveModal}
            style={{
              padding: "10px 20px",
              borderRadius: 8,
              border: "none",
              backgroundColor: "#1e88e5",
              color: "white",
              cursor: "pointer",
              fontWeight: "600",
              fontSize: "1rem",
              boxShadow: "0 3px 6px rgba(30,136,229,0.5)",
              transition: "background-color 0.3s ease",
            }}
            onMouseOver={(e) =>
              (e.currentTarget.style.backgroundColor = "#1565c0")
            }
            onMouseOut={(e) =>
              (e.currentTarget.style.backgroundColor = "#1e88e5")
            }
          >
            Guardar
          </button>
        </div>
      </Modal>
    </div>
  );
};

export default AdminPanel;
