// frontend/src/components/AdminPanel.jsx
import React, { useState, useMemo, useEffect, useRef } from "react";
import Modal from "react-modal";
import Select from "react-select";
import MiscellaneumClasses from "./admin/MiscellaneumClasses";

Modal.setAppElement("#root");

const SPECIAL_TAIL = new Set(["Bard", "Paladin", "Artist", "Valkyrie"]);
const ACTIVE_STATUSES = new Set(["upcoming", "live"]); // ← Estados que consideramos "activos"

const sortTeam = (arr) => {
  const withIndex = (arr || []).map((c, i) => ({ c, i }));
  withIndex.sort((a, b) => {
    const aIsTail = SPECIAL_TAIL.has(a.c?.class?.name);
    const bIsTail = SPECIAL_TAIL.has(b.c?.class?.name);
    if (aIsTail !== bIsTail) return aIsTail ? 1 : -1;
    return a.i - b.i;
  });
  return withIndex.map((x) => x.c);
};

// ---------- Helpers de fecha (formato HomePage: MM-DD) ----------
const z2 = (n) => String(n).padStart(2, "0");
const mmddRegex = /^(0[1-9]|1[0-2])-(0[1-9]|[12]\d|3[01])$/;
const ymdRegex  = /^(\d{4})-(0[1-9]|1[0-2])-(0[1-9]|[12]\d|3[01])$/;

const toMMDD = (s) => {
  if (!s || typeof s !== "string") return "";
  const t = s.trim();
  if (mmddRegex.test(t)) return t;
  if (/^(0[1-9]|1[0-2])\/(0[1-9]|[12]\d|3[01])$/.test(t)) {
    const [m, d] = t.split("/");
    return `${m}-${d}`;
  }
  const ymd = t.match(ymdRegex);
  if (ymd) {
    const [, , m, d] = ymd;
    return `${m}-${d}`;
  }
  return t;
};

const isValidMMDD = (mmdd) => {
  if (!mmddRegex.test(mmdd)) return false;
  const [m, d] = mmdd.split("-").map((x) => parseInt(x, 10));
  const date = new Date(2001, m - 1, d);
  return date.getMonth() + 1 === m && date.getDate() === d;
};

// ---------- Identidad por NOMBRE ----------
const nameKey = (c) => String(c?.name || "").trim().toLowerCase();

const dedupeCharsByName = (arr = []) => {
  const seen = new Set();
  const out = [];
  for (const c of arr) {
    const k = nameKey(c);
    if (!k || seen.has(k)) continue;
    seen.add(k);
    out.push(c);
  }
  return out;
};

const dedupeOptionsByName = (opts = []) => {
  const seen = new Set();
  const out = [];
  for (const o of opts) {
    const k = nameKey(o?.value);
    if (!k || seen.has(k)) continue;
    seen.add(k);
    out.push(o);
  }
  return out;
};

// ---------- Mini Calendar (sin año) ----------
const MONTHS = ["Ene","Feb","Mar","Abr","May","Jun","Jul","Ago","Sep","Oct","Nov","Dic"];
const WD     = ["D","L","M","M","J","V","S"]; // Domingo primero
function buildMonthMatrix(month /*1-12*/) {
  const first = new Date(2001, month - 1, 1);
  const firstWd = first.getDay();
  const daysInMonth = new Date(2001, month, 0).getDate();
  const cells = [];
  for (let i = 0; i < firstWd; i++) cells.push(null);
  for (let d = 1; d <= daysInMonth; d++) cells.push(d);
  while (cells.length % 7 !== 0) cells.push(null);
  const rows = [];
  for (let i = 0; i < cells.length; i += 7) rows.push(cells.slice(i, i + 7));
  return rows;
}

const AdminPanel = ({ cards, allCharacters, onSaveCards }) => {
  // Header
  const [createTitle, setCreateTitle] = useState("");

  // Modal state
  const [modalOpen, setModalOpen] = useState(false);
  const [editingCardId, setEditingCardId] = useState(null);

  // Confirmación "Eliminar todas"
  const [confirmAllOpen, setConfirmAllOpen] = useState(false);

  // Campos del modal
  const [modalTitle, setModalTitle] = useState("");
  const [selectedDate, setSelectedDate] = useState(""); // MM-DD
  const [selectedTime, setSelectedTime] = useState("");

  // Calendar popover
  const [showCal, setShowCal] = useState(false);
  const [calMonth, setCalMonth] = useState(() => new Date().getMonth() + 1);
  const calRef = useRef(null);

  // Select por equipo
  const [selectedTeam1, setSelectedTeam1] = useState([]); // [{value,label}]
  const [selectedTeam2, setSelectedTeam2] = useState([]);

  // ---- Nombres bloqueados globales por "tarjetas activas" (upcoming|live) ----
  const blockedActiveNames = useMemo(() => {
    const set = new Set();
    (cards || []).forEach((c) => {
      const st = String(c?.status || "").toLowerCase();
      if (!ACTIVE_STATUSES.has(st)) return;
      if (c?.id === editingCardId) return; // al editar, permitimos los ya presentes en esta tarjeta
      const pool = Array.isArray(c?.characters)
        ? c.characters
        : [...(c?.team1 || []), ...(c?.team2 || [])];
      pool.forEach((ch) => {
        const k = nameKey(ch);
        if (k) set.add(k);
      });
    });
    return set;
  }, [cards, editingCardId]);

  // Opciones base
  const baseOptions = useMemo(
    () =>
      (allCharacters ?? []).map((char) => ({
        value: char,
        label: char.name,
      })),
    [allCharacters]
  );

  // Nombres ya elegidos por cada team en el modal actual
  const selectedTeam1Names = useMemo(
    () => new Set(selectedTeam1.map((o) => nameKey(o.value))),
    [selectedTeam1]
  );
  const selectedTeam2Names = useMemo(
    () => new Set(selectedTeam2.map((o) => nameKey(o.value))),
    [selectedTeam2]
  );

  // Nombres actualmente permitidos (bloqueados menos los que ya están elegidos en este modal)
  const effectiveBlocked = useMemo(() => {
    const s = new Set(blockedActiveNames);
    selectedTeam1.forEach((o) => s.delete(nameKey(o.value)));
    selectedTeam2.forEach((o) => s.delete(nameKey(o.value)));
    return s;
  }, [blockedActiveNames, selectedTeam1, selectedTeam2]);

  // Filtra/deduplica opciones visibles por team (sin cruzarse entre equipos y sin "bloqueados por activo")
  const optionsTeam1 = useMemo(() => {
    const filtered = baseOptions.filter((opt) => {
      const k = nameKey(opt.value);
      if (effectiveBlocked.has(k)) return false;
      return !selectedTeam2Names.has(k);
    });
    return dedupeOptionsByName(filtered);
  }, [baseOptions, selectedTeam2Names, effectiveBlocked]);

  const optionsTeam2 = useMemo(() => {
    const filtered = baseOptions.filter((opt) => {
      const k = nameKey(opt.value);
      if (effectiveBlocked.has(k)) return false;
      return !selectedTeam1Names.has(k);
    });
    return dedupeOptionsByName(filtered);
  }, [baseOptions, selectedTeam1Names, effectiveBlocked]);

  // Cerrar calendario al hacer click afuera
  useEffect(() => {
    const onDocClick = (e) => {
      if (!showCal) return;
      if (calRef.current && !calRef.current.contains(e.target)) setShowCal(false);
    };
    document.addEventListener("mousedown", onDocClick);
    return () => document.removeEventListener("mousedown", onDocClick);
  }, [showCal]);

  // Carga de equipos (sanea duplicados y cruces)
  const loadTeamsFromCard = (card) => {
    const fallbackChars = Array.isArray(card?.characters) ? card.characters : [];
    const t1raw = Array.isArray(card?.team1) ? card.team1 : fallbackChars.slice(0, 4);
    const t2raw = Array.isArray(card?.team2) ? card.team2 : fallbackChars.slice(4, 8);

    const t1 = dedupeCharsByName(sortTeam(t1raw)).slice(0, 4);
    const t1Names = new Set(t1.map(nameKey));
    const t2 = dedupeCharsByName(sortTeam(t2raw).filter((c) => !t1Names.has(nameKey(c)))).slice(0, 4);

    setSelectedTeam1(t1.map((c) => ({ value: c, label: c.name })));
    setSelectedTeam2(t2.map((c) => ({ value: c, label: c.name })));
  };

  const openModal = (card) => {
    setEditingCardId(card?.id || null);
    setModalTitle(card?.title || createTitle || "");
    const mmdd = toMMDD(card?.date || "");
    setSelectedDate(mmdd);
    setSelectedTime(card?.time || "");
    if (mmddRegex.test(mmdd)) {
      const [m] = mmdd.split("-");
      setCalMonth(parseInt(m, 10));
    } else {
      setCalMonth(new Date().getMonth() + 1);
    }
    loadTeamsFromCard(card);
    setModalOpen(true);
  };

  // Guardar con validación fuerte (sin repetidos por nombre y sin usar bloqueados por "activos")
  const saveModal = () => {
    const trimmedTitle = (modalTitle || createTitle || "").trim();
    if (!trimmedTitle) {
      alert("Debe ingresar un título para la tarjeta");
      return;
    }
    const normalized = toMMDD(selectedDate);
    if (normalized && !isValidMMDD(normalized)) {
      alert("La fecha debe estar en formato MM-DD y ser válida. Ej: 08-16");
      return;
    }

    const team1 = dedupeCharsByName(sortTeam((selectedTeam1 || []).map((o) => o.value))).slice(0, 4);
    const team2Pre = dedupeCharsByName(sortTeam((selectedTeam2 || []).map((o) => o.value))).slice(0, 4);

    // Bloqueo cruzado: nombres del team2 no pueden estar en team1
    const t1Names = new Set(team1.map(nameKey));
    const team2 = team2Pre.filter((c) => !t1Names.has(nameKey(c))).slice(0, 4);

    // Validación de bloqueados por tarjetas activas (permitimos los ya presentes en esta tarjeta si se está editando)
    const currentNames = new Set([...team1, ...team2].map(nameKey));
    const conflicts = [];
    currentNames.forEach((k) => {
      if (blockedActiveNames.has(k)) conflicts.push(k);
    });
    if (conflicts.length) {
      alert(
        `No puedes usar personajes que ya están en tarjetas ACTIVAS (upcoming/live).\nBloqueados: ${conflicts.join(", ")}`
      );
      return;
    }

    // Validación final de duplicados entre equipos
    const seen = new Set();
    const dups = [];
    for (const c of [...team1, ...team2]) {
      const k = nameKey(c);
      if (seen.has(k)) dups.push(c?.name || "(sin nombre)");
      else seen.add(k);
    }
    if (dups.length) {
      alert(
        `Un mismo personaje no puede repetirse entre Team 1 y Team 2.\nDuplicados: ${[
          ...new Set(dups),
        ].join(", ")}`
      );
      return;
    }

    if (team1.length + team2.length === 0) {
      alert("Debe seleccionar al menos un personaje (en team1 o team2)");
      return;
    }

    const characters = dedupeCharsByName([...team1, ...team2]);

    const newCardData = {
      title: trimmedTitle,
      date: normalized,
      time: selectedTime,
      team1,
      team2,
      characters,
      // status: card?.status ?? "upcoming"  // si quieres setear por defecto
    };

    if (editingCardId) {
      const updated = (cards || []).map((c) => (c.id === editingCardId ? { ...c, ...newCardData } : c));
      onSaveCards(updated);
    } else {
      const newCard = { id: Date.now(), ...newCardData };
      onSaveCards([...(cards || []), newCard]);
      setCreateTitle("");
    }
    setModalOpen(false);
    setShowCal(false);
  };

  const deleteCard = (cardId) => {
    if (window.confirm("¿Seguro que quieres eliminar esta tarjeta?")) {
      onSaveCards((cards || []).filter((c) => c.id !== cardId));
    }
  };

  // Eliminar todas (confirm)
  const openDeleteAllConfirm  = () => setConfirmAllOpen(true);
  const closeDeleteAllConfirm = () => setConfirmAllOpen(false);
  const confirmDeleteAll      = () => { onSaveCards([]); setConfirmAllOpen(false); };

  // onTeamChange con dedupe, bloqueo cruzado y bloqueo por activos
  const onTeamChange = (teamKey, value) => {
    const incoming = dedupeOptionsByName((value || []).slice(0, 4));

    // Filtra por nombres bloqueados (activos) y cruce de equipos
    if (teamKey === "team1") {
      const filtered = incoming
        .filter((o) => {
          const k = nameKey(o.value);
          return !blockedActiveNames.has(k) && !selectedTeam2Names.has(k);
        })
        .slice(0, 4);
      setSelectedTeam1(dedupeOptionsByName(filtered));
    } else {
      const filtered = incoming
        .filter((o) => {
          const k = nameKey(o.value);
          return !blockedActiveNames.has(k) && !selectedTeam1Names.has(k);
        })
        .slice(0, 4);
      setSelectedTeam2(dedupeOptionsByName(filtered));
    }
  };

  // Entrada de fecha
  const handleDateInput = (e) => {
    let v = e.target.value.replace(/[^\d\-\/]/g, "");
    v = v.replace(/\//g, "-");
    if (v.length > 5) v = v.slice(0, 5);
    if (/^\d{3,4}$/.test(v) && !v.includes("-")) v = `${v.slice(0, 2)}-${v.slice(2)}`;
    setSelectedDate(v);
    if (/^\d{2}-?$/.test(v)) {
      const m = parseInt(v.slice(0, 2), 10);
      if (m >= 1 && m <= 12) setCalMonth(m);
    }
  };

  const pickDay = (day) => {
    const mmdd = `${z2(calMonth)}-${z2(day)}`;
    setSelectedDate(mmdd);
    setShowCal(false);
  };

  const monthMatrix = buildMonthMatrix(calMonth);

  // ---- ORDEN tarjetas (fecha -> hora -> título) ----
  const sortedCards = useMemo(() => {
    const dateKeyFromDate = (dateStr) => {
      if (!dateStr) return Number.POSITIVE_INFINITY;
      const s = String(dateStr).trim();
      if (mmddRegex.test(s)) {
        const [m, d] = s.split("-");
        return parseInt(`${m}${d}`, 10);
      }
      const ymd = s.match(ymdRegex);
      if (ymd) {
        const [, , m, d] = ymd;
        return parseInt(`${m}${d}`, 10);
      }
      const d = new Date(s);
      if (!isNaN(d)) {
        const mm = String(d.getMonth() + 1).padStart(2, "0");
        const dd = String(d.getDate()).padStart(2, "0");
        return parseInt(`${mm}${dd}`, 10);
      }
      return Number.POSITIVE_INFINITY;
    };

    const minutesFromTime = (timeStr) => {
      if (!timeStr) return Number.POSITIVE_INFINITY;
      const s = String(timeStr).trim();
      let m = s.match(/^([01]?\d|2[0-3]):([0-5]\d)$/);
      if (m) {
        const hh = parseInt(m[1], 10);
        const mm = parseInt(m[2], 10);
        return hh * 60 + mm;
      }
      m = s.match(/^(\d{3,4})$/); // 930 -> 09:30
      if (m) {
        const raw = m[1];
        const hh = parseInt(raw.length === 3 ? raw.slice(0, 1) : raw.slice(0, 2), 10);
        const mm = parseInt(raw.length === 3 ? raw.slice(1) : raw.slice(2), 10);
        if (hh >= 0 && hh <= 23 && mm >= 0 && mm <= 59) return hh * 60 + mm;
      }
      m = s.match(/^([01]?\d|2[0-3])$/); // "14" -> 14:00
      if (m) return parseInt(m[1], 10) * 60;
      return Number.POSITIVE_INFINITY;
    };

    const list = Array.isArray(cards) ? [...cards] : [];
    return list.sort((a, b) => {
      const da = dateKeyFromDate(a?.date);
      const db = dateKeyFromDate(b?.date);
      if (da !== db) return da - db;

      const ta = minutesFromTime(a?.time);
      const tb = minutesFromTime(b?.time);
      if (ta !== tb) return ta - tb;

      const at = (a?.title || "").toLowerCase();
      const bt = (b?.title || "").toLowerCase();
      if (at < bt) return -1;
      if (at > bt) return 1;
      return 0;
    });
  }, [cards]);

  return (
    <div
      style={{
        padding: 20,
        maxWidth: 1100,
        margin: "0 auto",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        color: "#34495e",
        position: "relative",
        minHeight: "100vh",
      }}
    >
      <h1 style={{ marginBottom: 20, color: "#1e88e5", fontWeight: "700" }}>Panel de Administración</h1>

      {/* Header creación */}
      <div style={{ marginBottom: 20, display: "flex", gap: 10 }}>
        <input
          type="text"
          placeholder="Título para nueva tarjeta"
          value={createTitle}
          onChange={(e) => setCreateTitle(e.target.value)}
          style={{
            flex: 1,
            padding: 10,
            borderRadius: 8,
            border: "1px solid #ccc",
            fontSize: "1rem",
            outline: "none",
          }}
        />
        <button
          onClick={() => openModal(null)}
          style={{
            padding: "10px 20px",
            cursor: "pointer",
            backgroundColor: "#1e88e5",
            color: "white",
            border: "none",
            borderRadius: 8,
            fontWeight: "600",
            fontSize: "1rem",
            boxShadow: "0 3px 6px rgba(30,136,229,0.5)",
            transition: "background-color 0.3s ease",
          }}
          onMouseOver={(e) => (e.currentTarget.style.backgroundColor = "#1565c0")}
          onMouseOut={(e) => (e.currentTarget.style.backgroundColor = "#1e88e5")}
        >
          Crear Tarjeta
        </button>

        {/* Eliminar todas */}
        <button
          onClick={openDeleteAllConfirm}
          disabled={!cards || cards.length === 0}
          title="Eliminar todas las tarjetas"
          style={{
            padding: "10px 16px",
            cursor: !cards || cards.length === 0 ? "not-allowed" : "pointer",
            backgroundColor: !cards || cards.length === 0 ? "#ef9a9a" : "#d32f2f",
            color: "white",
            border: "none",
            borderRadius: 8,
            fontWeight: "700",
            fontSize: "0.95rem",
            boxShadow: "0 3px 6px rgba(211,47,47,0.4)",
            transition: "background-color 0.3s ease, opacity 0.3s ease",
            opacity: !cards || cards.length === 0 ? 0.7 : 1,
          }}
          onMouseOver={(e) => {
            if (cards && cards.length > 0) e.currentTarget.style.backgroundColor = "#9a2424";
          }}
          onMouseOut={(e) => {
            if (cards && cards.length > 0) e.currentTarget.style.backgroundColor = "#d32f2f";
          }}
        >
          Eliminar todas
        </button>
      </div>

      {/* Grid de tarjetas */}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(3, 1fr)",
          gap: 24,
          justifyItems: "stretch",
        }}
      >
        {(!sortedCards || sortedCards.length === 0) ? (
          <p>No hay tarjetas creadas</p>
        ) : (
          sortedCards.map((card) => (
            <div
              key={card.id}
              style={{
                borderRadius: 12,
                boxShadow: "0 12px 20px rgba(0,0,0,0.08)",
                backgroundColor: "#f0f5ff",
                display: "flex",
                flexDirection: "column",
                padding: 12,
                justifyContent: "space-between",
                cursor: "default",
                width: "100%",
              }}
            >
              <div>
                <h3
                  style={{
                    color: "#1e88e5",
                    fontWeight: "700",
                    marginBottom: 6,
                    whiteSpace: "nowrap",
                    overflow: "hidden",
                    textOverflow: "ellipsis",
                  }}
                  title={card.title}
                >
                  {card.title}
                </h3>

                {/* Fecha y hora en una línea */}
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                    fontSize: "0.9rem",
                    color: "#607d8b",
                    fontWeight: 600,
                    marginTop: 2,
                  }}
                >
                  <span>{toMMDD(card.date) || "No asignada"}</span>
                  <span>{card.time || "No asignada"}</span>
                </div>

                {/* Chips de personajes */}
                <div style={{ marginTop: 10 }}>
                  <strong style={{ fontWeight: "700", color: "#0d47a1" }}>Personajes:</strong>
                  <div style={{ display: "flex", flexWrap: "wrap", gap: 6, marginTop: 6 }}>
                    {(card.characters ?? []).map((c, i) => (
                      <span
                        key={i}
                        style={{
                          display: "inline-flex",
                          alignItems: "center",
                          gap: 6,
                          backgroundColor: "#bbdefb",
                          padding: "5px 10px",
                          borderRadius: 20,
                          fontSize: "0.9em",
                          fontWeight: 600,
                          color: "#0d47a1",
                          userSelect: "none",
                          boxShadow: "0 1px 4px rgba(0,71,173,0.3)",
                        }}
                      >
                        {c.class?.image && (
                          <img
                            src={c.class.image}
                            alt={c.class?.name || "Clase"}
                            style={{ width: 18, height: 18, borderRadius: "50%" }}
                          />
                        )}
                        {c.name}
                      </span>
                    ))}
                  </div>
                </div>
              </div>

              <div style={{ marginTop: 14, display: "flex", gap: 8 }}>
                <button
                  onClick={() => openModal(card)}
                  style={{
                    flex: 1,
                    padding: "8px 0",
                    cursor: "pointer",
                    borderRadius: 8,
                    border: "none",
                    backgroundColor: "#1976d2",
                    color: "white",
                    fontWeight: "600",
                    transition: "background-color 0.3s ease",
                  }}
                  onMouseOver={(e) => (e.currentTarget.style.backgroundColor = "#115293")}
                  onMouseOut={(e) => (e.currentTarget.style.backgroundColor = "#1976d2")}
                >
                  Editar Tarjeta
                </button>

                <button
                  onClick={() => deleteCard(card.id)}
                  style={{
                    flex: 1,
                    padding: "8px 0",
                    cursor: "pointer",
                    borderRadius: 8,
                    border: "none",
                    backgroundColor: "#d32f2f",
                    color: "white",
                    fontWeight: "600",
                    transition: "background-color 0.3s ease",
                  }}
                  onMouseOver={(e) => (e.currentTarget.style.backgroundColor = "#9a2424")}
                  onMouseOut={(e) => (e.currentTarget.style.backgroundColor = "#d32f2f")}
                >
                  Eliminar Tarjeta
                </button>
              </div>
            </div>
          ))
        )}
      </div>

      {/* MODAL: Crear/Editar tarjeta */}
      <Modal
        isOpen={modalOpen}
        onRequestClose={() => { setModalOpen(false); setShowCal(false); }}
        contentLabel="Configurar Tarjeta"
        style={{
          content: {
            maxWidth: "720px",
            margin: "auto",
            height: "fit-content",
            padding: "20px",
            borderRadius: "12px",
            boxShadow: "0 10px 30px rgba(0,0,0,0.15)",
            zIndex: 1000,
          },
          overlay: { zIndex: 999 },
        }}
      >
        <h2 style={{ marginBottom: 16 }}>{editingCardId ? "Editar Tarjeta" : "Nueva Tarjeta"}</h2>

        {/* Título */}
        <input
          type="text"
          placeholder="Título para la tarjeta"
          value={modalTitle}
          onChange={(e) => setModalTitle(e.target.value)}
          style={{
            width: "100%",
            padding: 12,
            marginBottom: 16,
            borderRadius: 8,
            border: "1px solid #ccc",
            fontSize: "1rem",
            outline: "none",
          }}
        />

        {/* Fecha (MM-DD) + Calendario / Hora */}
        <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 12, marginBottom: 16 }}>
          <div style={{ position: "relative" }}>
            <label style={{ display: "block" }}>
              Fecha (MM-DD):
              <div style={{ display: "flex", gap: 8, marginTop: 6 }}>
                <input
                  type="text"
                  inputMode="numeric"
                  placeholder="MM-DD"
                  value={selectedDate}
                  onChange={handleDateInput}
                  onBlur={() => setSelectedDate(toMMDD(selectedDate))}
                  style={{
                    width: "100%",
                    padding: 10,
                    borderRadius: 8,
                    border: "1px solid #ccc",
                    fontSize: "1rem",
                    letterSpacing: "0.5px",
                  }}
                />
                <button
                  type="button"
                  onClick={() => setShowCal((s) => !s)}
                  aria-label="Abrir calendario"
                  style={{
                    padding: "0 12px",
                    borderRadius: 8,
                    border: "1px solid #ccc",
                    background: "#f8fafc",
                    cursor: "pointer",
                    fontWeight: 600,
                  }}
                >
                  📅
                </button>
              </div>
            </label>

            {showCal && (
              <div
                ref={calRef}
                style={{
                  position: "absolute",
                  top: "64px",
                  left: 0,
                  background: "white",
                  border: "1px solid #e2e8f0",
                  borderRadius: 10,
                  boxShadow: "0 10px 20px rgba(0,0,0,0.12)",
                  padding: 10,
                  zIndex: 1100,
                  width: 280,
                }}
              >
                <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between", marginBottom: 8 }}>
                  <button
                    onClick={() => setCalMonth((m) => (m === 1 ? 12 : m - 1))}
                    style={{ border: "none", background: "transparent", cursor: "pointer", fontSize: 18 }}
                    aria-label="Mes anterior"
                  >
                    ◀
                  </button>
                  <div style={{ fontWeight: 700 }}>{MONTHS[calMonth - 1]}</div>
                  <button
                    onClick={() => setCalMonth((m) => (m === 12 ? 1 : m + 1))}
                    style={{ border: "none", background: "transparent", cursor: "pointer", fontSize: 18 }}
                    aria-label="Mes siguiente"
                  >
                    ▶
                  </button>
                </div>
                <div style={{ display: "grid", gridTemplateColumns: "repeat(7, 1fr)", gap: 4, marginBottom: 6 }}>
                  {WD.map((w) => (
                    <div key={w} style={{ textAlign: "center", fontSize: 12, color: "#64748b" }}>{w}</div>
                  ))}
                </div>
                <div style={{ display: "grid", gridTemplateColumns: "repeat(7, 1fr)", gap: 4 }}>
                  {buildMonthMatrix(calMonth).map((row, ri) =>
                    row.map((cell, ci) => {
                      const key = `${ri}-${ci}`;
                      if (cell === null) return <div key={key} style={{ height: 32 }} />;
                      const mmdd = `${z2(calMonth)}-${z2(cell)}`;
                      const isSelected = selectedDate === mmdd;
                      return (
                        <button
                          key={key}
                          onClick={() => pickDay(cell)}
                          style={{
                            height: 32,
                            borderRadius: 6,
                            border: "1px solid #e2e8f0",
                            background: isSelected ? "#1e88e5" : "white",
                            color: isSelected ? "white" : "#0f172a",
                            cursor: "pointer",
                            fontWeight: 600,
                          }}
                        >
                          {cell}
                        </button>
                      );
                    })
                  )}
                </div>
              </div>
            )}
          </div>

          <label style={{ display: "block" }}>
            Hora:
            <input
              type="time"
              value={selectedTime}
              onChange={(e) => setSelectedTime(e.target.value)}
              style={{
                width: "100%",
                padding: 10,
                marginTop: 6,
                borderRadius: 8,
                border: "1px solid #ccc",
                fontSize: "1rem",
              }}
            />
          </label>
        </div>

        {/* Equipos lado a lado */}
        <div style={{ fontWeight: 600, marginBottom: 8 }}>Selecciona personajes por equipo (máx 4 por equipo):</div>

        <div
          style={{
            display: "grid",
            gridTemplateColumns: "1fr 1fr",
            gap: 16,
            alignItems: "start",
          }}
        >
          {/* TEAM 1 */}
          <div>
            <div style={{ display: "flex", justifyContent: "space-between", marginBottom: 6 }}>
              <span style={{ fontWeight: 700, color: "#0f172a" }}>Team 1</span>
              <span style={{ fontSize: 12, color: "#64748b" }}>{selectedTeam1.length}/4</span>
            </div>
            <Select
              isMulti
              options={optionsTeam1}
              value={selectedTeam1}
              onChange={(v) => onTeamChange("team1", v)}
              placeholder="Buscar personajes..."
              closeMenuOnSelect={false}
              getOptionValue={(opt) => nameKey(opt.value)}
              isOptionDisabled={(opt) => {
                const k = nameKey(opt.value);
                return effectiveBlocked.has(k) || selectedTeam1Names.has(k) || selectedTeam2Names.has(k);
              }}
              styles={{
                control: (p) => ({ ...p, borderRadius: 8, minHeight: 40, fontSize: "0.95rem" }),
                multiValue: (p) => ({ ...p, backgroundColor: "#bbdefb", borderRadius: 20 }),
                multiValueLabel: (p) => ({ ...p, color: "#0d47a1", fontWeight: 600 }),
                multiValueRemove: (p) => ({
                  ...p,
                  color: "#0d47a1",
                  ":hover": { backgroundColor: "#0d47a1", color: "white" },
                }),
              }}
            />
            <div style={{ display: "grid", gridTemplateRows: "repeat(4, minmax(0, auto))", gap: 8, marginTop: 10 }}>
              {sortTeam(selectedTeam1.map((x) => x.value)).slice(0, 4).map((c, i) => (
                <div
                  key={`t1-${i}-${c?.name || i}`}
                  style={{
                    background: "#f8fafc",
                    border: "1px solid #e2e8f0",
                    borderRadius: 10,
                    padding: 8,
                    display: "flex",
                    alignItems: "center",
                    gap: 8,
                  }}
                >
                  {c?.class?.image && (
                    <img src={c.class.image} alt={c.class.name} style={{ width: 20, height: 20, borderRadius: 6 }} />
                  )}
                  <div style={{ fontSize: 13, fontWeight: 600 }}>{c?.name}</div>
                  <div style={{ fontSize: 11, color: "#64748b", marginLeft: "auto" }}>{c?.class?.name}</div>
                </div>
              ))}
            </div>
          </div>

          {/* TEAM 2 */}
          <div>
            <div style={{ display: "flex", justifyContent: "space-between", marginBottom: 6 }}>
              <span style={{ fontWeight: 700, color: "#0f172a" }}>Team 2</span>
              <span style={{ fontSize: 12, color: "#64748b" }}>{selectedTeam2.length}/4</span>
            </div>
            <Select
              isMulti
              options={optionsTeam2}
              value={selectedTeam2}
              onChange={(v) => onTeamChange("team2", v)}
              placeholder="Buscar personajes..."
              closeMenuOnSelect={false}
              getOptionValue={(opt) => nameKey(opt.value)}
              isOptionDisabled={(opt) => {
                const k = nameKey(opt.value);
                return effectiveBlocked.has(k) || selectedTeam1Names.has(k) || selectedTeam2Names.has(k);
              }}
              styles={{
                control: (p) => ({ ...p, borderRadius: 8, minHeight: 40, fontSize: "0.95rem" }),
                multiValue: (p) => ({ ...p, backgroundColor: "#bbdefb", borderRadius: 20 }),
                multiValueLabel: (p) => ({ ...p, color: "#0d47a1", fontWeight: 600 }),
                multiValueRemove: (p) => ({
                  ...p,
                  color: "#0d47a1",
                  ":hover": { backgroundColor: "#0d47a1", color: "white" },
                }),
              }}
            />
            <div style={{ display: "grid", gridTemplateRows: "repeat(4, minmax(0, auto))", gap: 8, marginTop: 10 }}>
              {sortTeam(selectedTeam2.map((x) => x.value)).slice(0, 4).map((c, i) => (
                <div
                  key={`t2-${i}-${c?.name || i}`}
                  style={{
                    background: "#f8fafc",
                    border: "1px solid #e2e8f0",
                    borderRadius: 10,
                    padding: 8,
                    display: "flex",
                    alignItems: "center",
                    gap: 8,
                  }}
                >
                  {c?.class?.image && (
                    <img src={c.class.image} alt={c.class.name} style={{ width: 20, height: 20, borderRadius: 6 }} />
                  )}
                  <div style={{ fontSize: 13, fontWeight: 600 }}>{c?.name}</div>
                  <div style={{ fontSize: 11, color: "#64748b", marginLeft: "auto" }}>{c?.class?.name}</div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Footer acciones */}
        <div style={{ marginTop: 20, display: "flex", justifyContent: "flex-end", gap: 12 }}>
          <button
            onClick={() => { setModalOpen(false); setShowCal(false); }}
            style={{
              padding: "10px 20px",
              borderRadius: 8,
              border: "1px solid #ccc",
              backgroundColor: "white",
              cursor: "pointer",
              fontWeight: "600",
              fontSize: "1rem",
            }}
          >
            Cancelar
          </button>
          <button
            onClick={saveModal}
            style={{
              padding: "10px 20px",
              borderRadius: 8,
              border: "none",
              backgroundColor: "#1e88e5",
              color: "white",
              cursor: "pointer",
              fontWeight: "600",
              fontSize: "1rem",
              boxShadow: "0 3px 6px rgba(30,136,229,0.5)",
              transition: "background-color 0.3s ease",
            }}
            onMouseOver={(e) => (e.currentTarget.style.backgroundColor = "#1565c0")}
            onMouseOut={(e) => (e.currentTarget.style.backgroundColor = "#1e88e5")}
          >
            Guardar
          </button>
        </div>
      </Modal>

      {/* MODAL: Confirmación eliminar todas */}
      <Modal
        isOpen={confirmAllOpen}
        onRequestClose={closeDeleteAllConfirm}
        contentLabel="Confirmar eliminación masiva"
        style={{
          content: {
            maxWidth: "520px",
            margin: "auto",
            height: "fit-content",
            padding: "22px",
            borderRadius: "12px",
            boxShadow: "0 10px 30px rgba(0,0,0,0.18)",
            zIndex: 1002,
          },
          overlay: { zIndex: 1001, backgroundColor: "rgba(0,0,0,0.45)" },
        }}
      >
        <h2 style={{ marginTop: 0, marginBottom: 10, color: "#d32f2f" }}>Eliminar todas las tarjetas</h2>
        <p style={{ margin: 0, color: "#374151" }}>
          Vas a eliminar <strong>{cards?.length || 0}</strong> tarjeta(s). Esta acción <strong>no se puede deshacer</strong>.
        </p>
        <p style={{ marginTop: 8, color: "#6b7280", fontSize: 14 }}>
          ¿Deseas continuar?
        </p>

        <div style={{ marginTop: 18, display: "flex", justifyContent: "flex-end", gap: 10 }}>
          <button
            onClick={closeDeleteAllConfirm}
            style={{
              padding: "10px 16px",
              borderRadius: 8,
              border: "1px solid #d1d5db",
              backgroundColor: "white",
              cursor: "pointer",
              fontWeight: 600,
            }}
          >
            Cancelar
          </button>
          <button
            onClick={confirmDeleteAll}
            style={{
              padding: "10px 16px",
              borderRadius: 8,
              border: "none",
              backgroundColor: "#d32f2f",
              color: "white",
              cursor: "pointer",
              fontWeight: 700,
              boxShadow: "0 3px 6px rgba(211,47,47,0.35)",
            }}
            onMouseOver={(e) => (e.currentTarget.style.backgroundColor = "#9a2424")}
            onMouseOut={(e) => (e.currentTarget.style.backgroundColor = "#d32f2f")}
          >
            Sí, eliminar todo
          </button>
        </div>
      </Modal>

      {/* Miscellaneum fijo */}
      <MiscellaneumClasses title="Clases" sortMode="asIs" />
    </div>
  );
};

export default AdminPanel;
